import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String selectedFilter = 'All';
  List<String> filters = ['All', 'Electronics', 'Fashion', 'Home', 'Books'];
  List<Color> rainbowColors = [Colors.red, Colors.orange, Colors.yellow, Colors.green, Colors.blue, Colors.indigo, Colors.purple];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(
            begin: Alignment.topLeft,
            end: Alignment.bottomRight,
            colors: [Colors.pink.shade100, Colors.purple.shade100, Colors.blue.shade100],
          ),
        ),
        child: Column(
          children: [
            AppBar(
              title: Text('Colorful Search', style: TextStyle(fontWeight: FontWeight.w600, color: Colors.purple.shade800)),
              backgroundColor: Colors.transparent,
              foregroundColor: Colors.purple.shade800,
              elevation: 0,
            ),
            Padding(
              padding: EdgeInsets.all(16.0),
              child: Container(
                decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(20),
                  gradient: LinearGradient(
                    colors: [Colors.pink.shade200, Colors.purple.shade200, Colors.blue.shade200],
                  ),
                  boxShadow: [
                    BoxShadow(
                      color: Colors.purple.withOpacity(0.3),
                      blurRadius: 10,
                      offset: Offset(0, 5),
                    ),
                  ],
                ),
                padding: EdgeInsets.all(2),
                child: TextField(
                  decoration: InputDecoration(
                    hintText: 'Search colorfully...',
                    hintStyle: TextStyle(color: Colors.grey.shade600),
                    prefixIcon: Icon(Icons.search, color: Colors.purple.shade700),
                    border: OutlineInputBorder(
                      borderRadius: BorderRadius.circular(18),
                      borderSide: BorderSide.none,
                    ),
                    filled: true,
                    fillColor: Colors.white.withOpacity(0.9),
                  ),
                ),
              ),
            ),
            Container(
              height: 50,
              child: ListView.builder(
                scrollDirection: Axis.horizontal,
                padding: EdgeInsets.symmetric(horizontal: 16),
                itemCount: filters.length,
                itemBuilder: (context, index) {
                  bool isSelected = selectedFilter == filters[index];
                  Color chipColor = rainbowColors[index % rainbowColors.length];
                  return Padding(
                    padding: EdgeInsets.only(right: 8),
                    child: Container(
                      decoration: BoxDecoration(
                        borderRadius: BorderRadius.circular(25),
                        gradient: isSelected ? LinearGradient(
                          colors: [chipColor.withOpacity(0.7), chipColor],
                        ) : null,
                        boxShadow: isSelected ? [
                          BoxShadow(
                            color: chipColor.withOpacity(0.5),
                            blurRadius: 8,
                            offset: Offset(0, 4),
                          ),
                        ] : [],
                      ),
                      child: FilterChip(
                        label: Text(filters[index]),
                        selected: isSelected,
                        onSelected: (selected) {
                          setState(() {
                            selectedFilter = filters[index];
                          });
                        },
                        backgroundColor: Colors.white.withOpacity(0.8),
                        selectedColor: Colors.transparent,
                        labelStyle: TextStyle(
                          color: isSelected ? Colors.white : chipColor,
                          fontWeight: FontWeight.bold,
                        ),
                      ),
                    ),
                  );
                },
              ),
            ),
            Expanded(
              child: GridView.builder(
                padding: EdgeInsets.all(16),
                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                  crossAxisCount: 2,
                  childAspectRatio: 0.75,
                  crossAxisSpacing: 12,
                  mainAxisSpacing: 12,
                ),
                itemCount: 8,
                itemBuilder: (context, index) {
                  Color cardColor = rainbowColors[index % rainbowColors.length];
                  return Container(
                    decoration: BoxDecoration(
                      borderRadius: BorderRadius.circular(20),
                      gradient: LinearGradient(
                        begin: Alignment.topLeft,
                        end: Alignment.bottomRight,
                        colors: [cardColor.withOpacity(0.3), cardColor.withOpacity(0.1)],
                      ),
                      boxShadow: [
                        BoxShadow(
                          color: cardColor.withOpacity(0.3),
                          blurRadius: 8,
                          offset: Offset(0, 4),
                        ),
                      ],
                    ),
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        Expanded(
                          flex: 3,
                          child: Container(
                            decoration: BoxDecoration(
                              gradient: LinearGradient(
                                colors: [cardColor.withOpacity(0.2), cardColor.withOpacity(0.4)],
                              ),
                              borderRadius: BorderRadius.vertical(top: Radius.circular(20)),
                            ),
                            child: Center(
                              child: Icon(Icons.image, size: 40, color: cardColor.shade700),
                            ),
                          ),
                        ),
                        Expanded(
                          flex: 2,
                          child: Padding(
                            padding: EdgeInsets.all(8),
                            child: Column(
                              crossAxisAlignment: CrossAxisAlignment.start,
                              children: [
                                Text(
                                  'Product ${index + 1}',
                                  style: TextStyle(fontWeight: FontWeight.bold, fontSize: 14, color: Colors.grey.shade800),
                                  maxLines: 1,
                                  overflow: TextOverflow.ellipsis,
                                ),
                                SizedBox(height: 4),
                                Container(
                                  padding: EdgeInsets.symmetric(horizontal: 6, vertical: 2),
                                  decoration: BoxDecoration(
                                    gradient: LinearGradient(colors: [Colors.orange, Colors.red]),
                                    borderRadius: BorderRadius.circular(10),
                                  ),
                                  child: Text(
                                    '\$${(index + 1) * 10}.99',
                                    style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold, fontSize: 12),
                                  ),
                                ),
                                SizedBox(height: 4),
                                Row(
                                  children: List.generate(5, (i) => Icon(
                                    Icons.star,
                                    size: 12,
                                    color: i < 4 ? rainbowColors[i % rainbowColors.length] : Colors.grey.shade400,
                                  )),
                                ),
                              ],
                            ),
                          ),
                        ),
                      ],
                    ),
                  );
                },
              ),
            ),
          ],
        ),
      ),
      floatingActionButton: Container(
        decoration: BoxDecoration(
          shape: BoxShape.circle,
          gradient: LinearGradient(
            colors: [Colors.pink, Colors.purple, Colors.blue],
          ),
          boxShadow: [
            BoxShadow(
              color: Colors.purple.withOpacity(0.5),
              blurRadius: 15,
              offset: Offset(0, 8),
            ),
          ],
        ),
        child: FloatingActionButton(
          onPressed: () {},
          backgroundColor: Colors.transparent,
          elevation: 0,
          child: Icon(Icons.tune, color: Colors.white),
        ),
      ),
    );
  }
}
