import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.pink.shade50,
      body: SafeArea(
        child: Column(
          children: [
            AppBar(
              title: Text('Achievements', style: TextStyle(color: Colors.pink.shade700, fontWeight: FontWeight.bold)),
              backgroundColor: Colors.white,
              centerTitle: true,
            ),
            Container(
              margin: EdgeInsets.all(20),
              padding: EdgeInsets.all(20),
              decoration: BoxDecoration(
                color: Colors.white,
                borderRadius: BorderRadius.circular(15),
                boxShadow: [BoxShadow(color: Colors.pink.shade100, blurRadius: 5)],
              ),
              child: Stack(
                alignment: Alignment.center,
                children: [
                  SizedBox(
                    width: 100,
                    height: 100,
                    child: CircularProgressIndicator(
                      value: 0.7,
                      strokeWidth: 8,
                      backgroundColor: Colors.grey.shade200,
                      valueColor: AlwaysStoppedAnimation<Color>(Colors.pink.shade700),
                    ),
                  ),
                  Text('14/20', style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Colors.pink.shade700)),
                ],
              ),
            ),
            Wrap(
              spacing: 10,
              runSpacing: 10,
              alignment: WrapAlignment.center,
              children: ['All', 'Gaming', 'Learning', 'Social'].map((category) => 
                Chip(
                  label: Text(category, style: TextStyle(color: Colors.pink.shade700)),
                  backgroundColor: Colors.white,
                  side: BorderSide(color: Colors.pink.shade700),
                ),
              ).toList(),
            ),
            Expanded(
              child: ListView.builder(
                padding: EdgeInsets.all(20),
                itemCount: 8,
                itemBuilder: (context, index) {
                  bool isCompleted = index < 4;
                  return Container(
                    margin: EdgeInsets.only(bottom: 15),
                    decoration: BoxDecoration(
                      color: Colors.white,
                      borderRadius: BorderRadius.circular(15),
                      border: Border.all(color: Colors.pink.shade700),
                      boxShadow: [BoxShadow(color: Colors.pink.shade100, blurRadius: 5)],
                    ),
                    child: Opacity(
                      opacity: isCompleted ? 1.0 : 0.6,
                      child: ListTile(
                        leading: CircleAvatar(
                          backgroundColor: isCompleted ? (index < 2 ? Colors.amber : index < 4 ? Colors.grey.shade400 : Colors.orange.shade700) : Colors.grey.shade300,
                          child: Icon(isCompleted ? Icons.star : Icons.lock, color: Colors.white),
                        ),
                        title: Text(
                          ['First Win', 'Quick Learner', 'Team Star', 'Socialite', 'Master', 'Explorer', 'Champion', 'Legend'][index],
                          style: TextStyle(fontWeight: FontWeight.bold, color: Colors.pink.shade700),
                        ),
                        subtitle: Text(
                          ['Win a game', 'Complete 5 lessons', 'Join a team', 'Connect with 10 friends', 'Master a skill', 'Explore 10 places', 'Win 10 games', 'Top leaderboard'][index],
                          style: TextStyle(fontSize: 12),
                        ),
                      ),
                    ),
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
}
