      body: Column(
        children: [
          Padding(
            padding: EdgeInsets.all(16),
            child: Text('$currentCard of $totalCards', style: TextStyle(fontSize: 16, color: Colors.grey[600])),
          ),
          Expanded(
            child: Center(
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  IconButton(
                    icon: Icon(Icons.arrow_back_ios, size: 30, color: Colors.blue),
                    onPressed: () {},
                  ),
                  Container(
                    width: 280,
                    height: 180,
                    decoration: BoxDecoration(
                      color: Colors.white,
                      borderRadius: BorderRadius.circular(12),
                      boxShadow: [
                        BoxShadow(
                          color: Colors.black.withOpacity(0.1),
                          spreadRadius: 2,
                          blurRadius: 8,
                          offset: Offset(0, 4),
                        ),
                      ],
                    ),
                    child: Center(
                      child: Padding(
                        padding: EdgeInsets.all(20),
