                IconButton(
                  icon: Icon(Icons.refresh, color: Colors.grey[600]),
                  onPressed: () {},
                ),
                IconButton(
                  icon: Icon(isFavorite ? Icons.favorite : Icons.favorite_border, color: isFavorite ? Colors.red : Colors.grey[600]),
                  onPressed: () {
                    setState(() {
                      isFavorite = !isFavorite;
