import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      body: SafeArea(
        child: Padding(
          padding: EdgeInsets.all(24.0),
          child: Column(
            children: [
              Text(
                'Compare Products',
                style: TextStyle(
                  fontSize: 24,
                  fontWeight: FontWeight.bold,
                  color: Colors.black87,
                  fontFamily: 'Lora',
                ),
              ),
              SizedBox(height: 24),
              Expanded(
                child: SingleChildScrollView(
                  child: Column(
                    children: [
                      _buildProductCard(
                        'Smartphone X',
                        '799',
                        4.7,
                        ['128GB Storage', '6.5" Display', 'Quad Camera', '5000mAh Battery'],
                      ),
                      SizedBox(height: 24),
                      _buildProductCard(
                        'Smartphone Y',
                        '899',
                        4.5,
                        ['256GB Storage', '6.7" Display', 'Triple Camera', '4500mAh Battery'],
                      ),
                      SizedBox(height: 24),
                      _buildProductCard(
                        'Smartphone Z',
                        '699',
                        4.3,
                        ['64GB Storage', '6.4" Display', 'Dual Camera', '4000mAh Battery'],
                      ),
                    ],
                  ),
                ),
              ),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildProductCard(String name, String price, double rating, List<String> specs) {
    return Container(
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(16),
        border: Border.all(color: Colors.grey[200]!),
      ),
      child: Padding(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Container(
              height: 220,
              decoration: BoxDecoration(
                color: Colors.grey[100],
                borderRadius: BorderRadius.circular(8),
              ),
              child: Center(
                child: Icon(Icons.phone_android, size: 80, color: Colors.grey[400]),
              ),
            ),
            SizedBox(height: 16),
            Text(
              name,
              style: TextStyle(
                fontSize: 18,
                fontWeight: FontWeight.w600,
                color: Colors.grey[800],
                fontFamily: 'Lora',
              ),
            ),
            SizedBox(height: 8),
            Text(
              price,
              style: TextStyle(
                fontSize: 20,
                fontWeight: FontWeight.w600,
                color: Colors.teal[600],
                fontFamily: 'Lora',
              ),
            ),
            SizedBox(height: 8),
            Row(
              children: List.generate(5, (index) => Icon(
                index < rating.floor() ? Icons.star : Icons.star_border,
                color: Colors.blue[600],
                size: 20,
              )),
            ),
            SizedBox(height: 16),
            GridView.count(
              crossAxisCount: 2,
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              childAspectRatio: 3,
              mainAxisSpacing: 8,
              crossAxisSpacing: 8,
              children: specs.map((spec) => Row(
                children: [
                  Icon(Icons.check, size: 16, color: Colors.teal),
                  SizedBox(width: 8),
                  Expanded(
                    child: Text(
                      spec,
                      style: TextStyle(
                        fontSize: 12,
                        color: Colors.grey[700],
                        fontFamily: 'Lora',
                      ),
                    ),
                  ),
                ],
              )).toList(),
            ),
            SizedBox(height: 20),
            ElevatedButton(
              onPressed: () {},
              style: ElevatedButton.styleFrom(
                backgroundColor: Colors.yellow[700],
                minimumSize: Size(double.infinity, 48),
                shape: RoundedRectangleBorder(
                  borderRadius: BorderRadius.circular(8),
                ),
              ),
              child: Text(
                'Add to Cart',
                style: TextStyle(
                  color: Colors.black87,
                  fontSize: 16,
                  fontWeight: FontWeight.w500,
                  fontFamily: 'Lora',
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }
}
