}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[50],
      appBar: AppBar(
        backgroundColor: Colors.blue[600],
        elevation: 0,
        title: Text('Budget Planner', style: TextStyle(color: Colors.white, fontSize: 20, fontWeight: FontWeight.bold)),
        centerTitle: true,
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Container(
              width: double.infinity,
              padding: EdgeInsets.all(20),
              decoration: BoxDecoration(
                color: Colors.white,
                borderRadius: BorderRadius.circular(16),
                boxShadow: [BoxShadow(color: Colors.grey.withOpacity(0.1), spreadRadius: 2, blurRadius: 8)],
              ),
              child: Column(
                children: [
                  Text('Total Balance', style: TextStyle(color: Colors.grey[600], fontSize: 16)),
                  SizedBox(height: 8),
                  Text('4,250.00', style: TextStyle(color: Colors.blue[600], fontSize: 32, fontWeight: FontWeight.bold)),
                  SizedBox(height: 16),
                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceAround,
                    children: [
                      Icon(Icons.arrow_back_ios, color: Colors.grey[400]),
                      Text('March 2024', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600)),
                      Icon(Icons.arrow_forward_ios, color: Colors.grey[400]),
                    ],
                  ),
                ],
              ),
            ),
            SizedBox(height: 24),
            Text('Categories', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.grey[800])),
            SizedBox(height: 16),
            GridView.count(
              crossAxisCount: 2,
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              mainAxisSpacing: 16,
              crossAxisSpacing: 16,
              childAspectRatio: 1.1,
              children: [
                _buildCategoryCard('Food', Icons.restaurant, Colors.orange, 450, 500, Colors.green),
                _buildCategoryCard('Transport', Icons.directions_car, Colors.blue, 280, 300, Colors.orange),
                _buildCategoryCard('Shopping', Icons.shopping_bag, Colors.purple, 180, 200, Colors.green),
                _buildCategoryCard('Bills', Icons.receipt_long, Colors.red, 520, 500, Colors.red),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Transactions', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.grey[800])),
            SizedBox(height: 16),
            Container(
              decoration: BoxDecoration(
                color: Colors.white,
                borderRadius: BorderRadius.circular(12),
                boxShadow: [BoxShadow(color: Colors.grey.withOpacity(0.1), spreadRadius: 1, blurRadius: 4)],
              ),
              child: Column(
                children: [
                  _buildTransactionItem('Starbucks', Icons.local_cafe, '-4.50', 'Today'),
                  _buildTransactionItem('Uber Ride', Icons.local_taxi, '-12.30', 'Yesterday'),
                  _buildTransactionItem('Grocery Store', Icons.shopping_cart, '-67.20', 'Mar 15'),
                  _buildTransactionItem('Salary', Icons.account_balance_wallet, '+3,200', 'Mar 14'),
                ],
              ),
            ),
          ],
        ),
      ),
      floatingActionButton: Column(
        mainAxisAlignment: MainAxisAlignment.end,
        children: [
          FloatingActionButton(
            heroTag: 'income',
