import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    final products = [
      {'name': 'Wireless Headphones', 'description': 'Premium quality sound', 'price': '99.99', 'icon': Icons.headphones},
      {'name': 'Smartphone', 'description': 'Latest technology device', 'price': '699.99', 'icon': Icons.phone_android},
      {'name': 'Laptop Computer', 'description': 'High performance machine', 'price': '1299.99', 'icon': Icons.laptop},
      {'name': 'Smart Watch', 'description': 'Track your fitness goals', 'price': '299.99', 'icon': Icons.watch},
      {'name': 'Gaming Console', 'description': 'Next-gen gaming experience', 'price': '499.99', 'icon': Icons.games},
      {'name': 'Bluetooth Speaker', 'description': 'Portable audio solution', 'price': '79.99', 'icon': Icons.speaker},
    ];

    return Scaffold(
      backgroundColor: Color(0xFFFAF7F0),
      appBar: AppBar(
        title: Text('Collection', style: TextStyle(color: Colors.brown[800], fontWeight: FontWeight.w300, fontSize: 24)),
        backgroundColor: Color(0xFFFAF7F0),
        elevation: 0,
      ),
      body: ListView.builder(
        padding: EdgeInsets.all(20),
        itemCount: products.length,
        itemBuilder: (context, index) {
          final product = products[index];
          return Container(
            margin: EdgeInsets.only(bottom: 20),
            padding: EdgeInsets.all(20),
            decoration: BoxDecoration(
              color: Colors.white,
              borderRadius: BorderRadius.circular(8),
              boxShadow: [
                BoxShadow(
                  color: Colors.grey.withOpacity(0.1),
                  blurRadius: 10,
                  offset: Offset(0, 4),
                ),
              ],
            ),
            child: Row(
              children: [
                Container(
                  width: 60,
                  height: 60,
                  decoration: BoxDecoration(
                    color: Colors.grey[100],
                    borderRadius: BorderRadius.circular(8),
                  ),
                  child: Icon(product['icon'] as IconData, size: 28, color: Colors.grey[600]),
                ),
                SizedBox(width: 20),
                Expanded(
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      Text(
                        product['name'] as String,
                        style: TextStyle(fontSize: 16, fontWeight: FontWeight.w600, color: Colors.brown[800]),
                      ),
                      SizedBox(height: 4),
                      Text(
                        product['description'] as String,
                        style: TextStyle(fontSize: 13, color: Colors.grey[600]),
                      ),
                    ],
                  ),
                ),
                Column(
                  crossAxisAlignment: CrossAxisAlignment.end,
                  children: [
                    Text(
                      product['price'] as String,
                      style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.brown[700]),
                    ),
                    SizedBox(height: 8),
                    TextButton(
                      onPressed: () {},
                      child: Text(
                        'Add to Cart',
                        style: TextStyle(color: Colors.brown[600], fontSize: 12),
                      ),
                    ),
                  ],
                ),
              ],
            ),
          );
        },
      ),
    );
  }
}
