import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> wishlistItems = [
    {'name': 'Wireless Headphones', 'price': '99.99', 'icon': Icons.headphones},
    {'name': 'Smartphone', 'price': '699.99', 'icon': Icons.phone_android},
    {'name': 'Laptop', 'price': '1299.99', 'icon': Icons.laptop},
    {'name': 'Watch', 'price': '299.99', 'icon': Icons.watch},
  ];

  void removeItem(int index) {
    setState(() {
      wishlistItems.removeAt(index);
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFFDE4E4),
      appBar: AppBar(
        backgroundColor: Colors.purple[300],
        shape: RoundedRectangleBorder(
          borderRadius: BorderRadius.vertical(bottom: Radius.circular(30)),
        ),
        title: Row(
          children: [
            Icon(Icons.favorite, color: Colors.purple[700], size: 24),
            SizedBox(width: 8),
            Text('Wishlist', style: TextStyle(color: Colors.white, fontSize: 20, fontFamily: 'ComicNeue')),
          ],
        ),
      ),
      body: wishlistItems.isEmpty
          ? Center(
              child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Icon(Icons.favorite_border, size: 80, color: Colors.purple[200]),
                  SizedBox(height: 16),
                  Text('Your wishlist is empty', style: TextStyle(fontSize: 18, color: Colors.purple[400], fontFamily: 'ComicNeue')),
                ],
              ),
            )
          : ListView.builder(
              padding: EdgeInsets.all(16),
              itemCount: wishlistItems.length,
              itemBuilder: (context, index) {
                final item = wishlistItems[index];
                return Container(
                  margin: EdgeInsets.only(bottom: 12),
                  decoration: BoxDecoration(
                    color: Colors.white,
                    borderRadius: BorderRadius.circular(12),
                    border: Border.all(style: BorderStyle.solid, width: 2, color: Colors.purple[200], strokeAlign: BorderSide.strokeAlignOutside),
                  ),
                  child: Padding(
                    padding: EdgeInsets.all(16),
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        Row(
                          children: [
                            Container(
                              width: 50,
                              height: 50,
                              decoration: BoxDecoration(
                                color: Colors.purple[50],
                                borderRadius: BorderRadius.circular(8),
                              ),
                              child: Icon(item['icon'], size: 28, color: Colors.purple[600]),
                            ),
                            SizedBox(width: 16),
                            Expanded(
                              child: Column(
                                crossAxisAlignment: CrossAxisAlignment.start,
                                children: [
                                  Text(
                                    item['name'],
                                    style: TextStyle(fontSize: 16, fontWeight: FontWeight.w500, fontFamily: 'ComicNeue'),
                                  ),
                                  SizedBox(height: 4),
                                  Text(
                                    item['price'],
                                    style: TextStyle(fontSize: 14, color: Colors.blue[400], fontWeight: FontWeight.w600),
                                  ),
                                ],
                              ),
                            ),
                          ],
                        ),
                        SizedBox(height: 8),
                        Align(
                          alignment: Alignment.bottomRight,
                          child: IconButton(
                            icon: Icon(Icons.favorite, color: Colors.purple[700], size: 24),
                            onPressed: () => removeItem(index),
                          ),
                        ),
                      ],
                    ),
                  ),
                );
              },
            ),
      floatingActionButton: FloatingActionButton(
        backgroundColor: Colors.blue[200],
        child: Icon(Icons.add, color: Colors.white),
        onPressed: () {},
      ),
    );
  }
}
