import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[200],
      appBar: AppBar(
        backgroundColor: Colors.black,
        elevation: 0,
        title: Text('Retro Admin', style: TextStyle(fontFamily: 'Courier', color: Colors.white, fontWeight: FontWeight.bold)),
        actions: [
          Icon(Icons.notifications, color: Colors.white),
          SizedBox(width: 16),
          CircleAvatar(
            backgroundImage: NetworkImage('https://via.placeholder.com/40'),
            radius: 18,
          ),
          SizedBox(width: 16),
        ],
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Row(
              children: [
                Expanded(child: _buildStatCard('Total Users', '2,134', Color(0xFF00FFFF))),
                SizedBox(width: 12),
                Expanded(child: _buildStatCard('Revenue', '\$67,430', Color(0xFF00FF00))),
                SizedBox(width: 12),
                Expanded(child: _buildStatCard('Orders', '1,056', Color(0xFFFF00FF))),
                SizedBox(width: 12),
                Expanded(child: _buildStatCard('Growth', '15.3%', Color(0xFF800080))),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Activities', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, fontFamily: 'Courier')),
            SizedBox(height: 16),
            ...List.generate(5, (index) => _buildActivityItem('User ${index + 1} performed action', '${index + 1}m ago')),
          ],
        ),
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        child: Icon(Icons.add, color: Colors.white),
        backgroundColor: Color(0xFFFF00FF),
      ),
    );
  }

  Widget _buildStatCard(String title, String value, Color color) {
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: color,
        borderRadius: BorderRadius.circular(4),
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(title, style: TextStyle(color: Colors.black, fontSize: 12, fontFamily: 'Courier')),
          SizedBox(height: 8),
          Text(value, style: TextStyle(color: Colors.black, fontSize: 20, fontWeight: FontWeight.bold, fontFamily: 'Courier')),
        ],
      ),
    );
  }

  Widget _buildActivityItem(String activity, String time) {
    return Padding(
      padding: EdgeInsets.only(bottom: 12),
      child: Row(
        children: [
          Container(
            decoration: BoxDecoration(
              image: DecorationImage(
                image: NetworkImage('https://via.placeholder.com/40'),
                fit: BoxFit.cover,
              ),
              borderRadius: BorderRadius.circular(4),
            ),
            width: 40,
            height: 40,
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(activity, style: TextStyle(fontWeight: FontWeight.w500, fontFamily: 'Courier')),
                Text(time, style: TextStyle(color: Colors.grey[600], fontSize: 12, fontFamily: 'Courier')),
              ],
            ),
          ),
        ],
      ),
    );
  }
}
