import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[200],
      body: Row(
        children: [
          Container(
            width: 80,
            color: Colors.white,
            child: Column(
              children: [
                SizedBox(height: 16),
                Text('Admin Hub', style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16)),
                SizedBox(height: 24),
                Icon(Icons.dashboard, color: Colors.blue),
                SizedBox(height: 16),
                Icon(Icons.people, color: Colors.blue),
                SizedBox(height: 16),
                Icon(Icons.settings, color: Colors.blue),
              ],
            ),
          ),
          Expanded(
            child: SingleChildScrollView(
              padding: EdgeInsets.all(16),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Row(
                    children: [
                      Expanded(child: _buildStatCard('Total Users', '2,134', Color(0xFFBBDEFB))),
                      SizedBox(width: 12),
                      Expanded(child: _buildStatCard('Revenue', '\$67,430', Color(0xFFC8E6C9))),
                      SizedBox(width: 12),
                      Expanded(child: _buildStatCard('Orders', '1,056', Color(0xFFFFCCBC))),
                      SizedBox(width: 12),
                      Expanded(child: _buildStatCard('Growth', '15.3%', Color(0xFFE1BEE7))),
                    ],
                  ),
                  SizedBox(height: 24),
                  Text('Recent Activities', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold)),
                  SizedBox(height: 16),
                  SingleChildScrollView(
                    scrollDirection: Axis.horizontal,
                    child: Row(
                      children: List.generate(5, (index) => _buildActivityItem('User ${index + 1} performed action', '${index + 1}m ago')),
                    ),
                  ),
                ],
              ),
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        child: Icon(Icons.add),
        backgroundColor: Colors.blue,
      ),
    );
  }

  Widget _buildStatCard(String title, String value, Color color) {
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: color,
        borderRadius: BorderRadius.circular(12),
        boxShadow: [BoxShadow(color: Colors.black12, blurRadius: 4, offset: Offset(0, 2))],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(title, style: TextStyle(color: Colors.black87, fontSize: 12)),
          SizedBox(height: 8),
          Text(value, style: TextStyle(color: Colors.black87, fontSize: 20, fontWeight: FontWeight.bold)),
        ],
      ),
    );
  }

  Widget _buildActivityItem(String activity, String time) {
    return Container(
      width: 200,
      margin: EdgeInsets.only(right: 12),
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(8),
      ),
      child: Row(
        children: [
          CircleAvatar(
            backgroundImage: NetworkImage('https://via.placeholder.com/40'),
            radius: 20,
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(activity, style: TextStyle(fontWeight: FontWeight.w500)),
                Text(time, style: TextStyle(color: Colors.grey[600], fontSize: 12)),
              ],
            ),
          ),
        ],
      ),
    );
  }
}
