import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[900],
      body: SingleChildScrollView(
        padding: EdgeInsets.all(12),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            SizedBox(height: 16),
            Text('Hello, Alex!', style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold, color: Colors.white)),
            SizedBox(height: 16),
            Column(
              children: [
                _buildMetricCard('3,456', 'Sales', Colors.pink[400]!, Colors.pink[900]!, Icons.trending_up),
                SizedBox(height: 8),
                _buildMetricCard('1,234', 'Users', Colors.cyan[400]!, Colors.cyan[900]!, Icons.person_add),
                SizedBox(height: 8),
                _buildMetricCard('95%', 'Retention', Colors.lime[400]!, Colors.lime[900]!, Icons.star),
                SizedBox(height: 8),
                _buildMetricCard('\$20K', 'Revenue', Colors.yellow[400]!, Colors.yellow[900]!, Icons.monetization_on),
              ],
            ),
            SizedBox(height: 20),
            Text('Recent Activity', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.white)),
            SizedBox(height: 8),
            _buildActivityItem(Icons.shopping_cart, 'New Sale', 'Order #9012 completed', '1 min ago', Colors.pink[400]!),
            _buildActivityItem(Icons.person, 'New User', 'Alex Smith joined', '4 min ago', Colors.cyan[400]!),
            _buildActivityItem(Icons.payment, 'Payment', '\$599.99 processed', '7 min ago', Colors.lime[400]!),
          ],
        ),
      ),
      bottomNavigationBar: BottomNavigationBar(
        backgroundColor: Colors.grey[850],
        selectedItemColor: Colors.pink[400],
        unselectedItemColor: Colors.grey[400],
        currentIndex: 0,
        items: [
          BottomNavigationBarItem(icon: Icon(Icons.home), label: 'Home'),
          BottomNavigationBarItem(icon: Icon(Icons.bar_chart), label: 'Stats'),
          BottomNavigationBarItem(icon: Icon(Icons.person), label: 'Profile'),
        ],
      ),
    );
  }

  Widget _buildMetricCard(String value, String label, Color bgColor, Color iconColor, IconData icon) {
    return Container(
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: bgColor,
        borderRadius: BorderRadius.circular(4),
        border: Border.all(color: iconColor),
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              Icon(icon, color: Colors.white, size: 24),
              Icon(Icons.more_vert, color: Colors.grey[400], size: 16),
            ],
          ),
          SizedBox(height: 8),
          Text(value, style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white)),
          Text(label, style: TextStyle(fontSize: 12, color: Colors.grey[300])),
        ],
      ),
    );
  }

  Widget _buildActivityItem(IconData icon, String title, String subtitle, String time, Color borderColor) {
    return Container(
      margin: EdgeInsets.only(bottom: 8),
      padding: EdgeInsets.all(10),
      decoration: BoxDecoration(
        color: Colors.grey[850],
        borderRadius: BorderRadius.circular(4),
        border: Border.all(color: borderColor),
      ),
      child: Row(
        children: [
          CircleAvatar(
            radius: 18,
            backgroundColor: borderColor.withOpacity(0.2),
            child: Icon(icon, color: Colors.white, size: 18),
          ),
          SizedBox(width: 10),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, color: Colors.white)),
                Text(subtitle, style: TextStyle(color: Colors.grey[400], fontSize: 12)),
              ],
            ),
          ),
          Text(time, style: TextStyle(color: Colors.grey[400], fontSize: 11)),
        ],
      ),
    );
  }
}
