import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        title: Text('Expense Tracker', style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold)),
        backgroundColor: Colors.black,
        elevation: 0,
        centerTitle: true,
      ),
      body: Column(
        children: [
          Container(
            margin: EdgeInsets.all(12),
            padding: EdgeInsets.all(20),
            decoration: BoxDecoration(
              color: Colors.green[900],
              borderRadius: BorderRadius.circular(12),
              boxShadow: [BoxShadow(color: Colors.green.withOpacity(0.3), blurRadius: 10, spreadRadius: 2)],
            ),
            child: Column(
              children: [
                Text('Current Balance', style: TextStyle(fontSize: 16, color: Colors.green[200])),
                SizedBox(height: 8),
                Text('2,450.00', style: TextStyle(fontSize: 32, fontWeight: FontWeight.bold, color: Colors.green[400])),
              ],
            ),
          ),
          Container(
            height: 200,
            padding: EdgeInsets.symmetric(horizontal: 12, vertical: 8),
            child: GridView.count(
              crossAxisCount: 2,
              crossAxisSpacing: 12,
              mainAxisSpacing: 12,
              shrinkWrap: true,
              children: [
                _buildCategoryCard('Food', Icons.restaurant, Colors.pink),
                _buildCategoryCard('Transport', Icons.directions_car, Colors.cyan),
                _buildCategoryCard('Shopping', Icons.shopping_bag, Colors.yellow),
                _buildCategoryCard('Bills', Icons.receipt, Colors.green),
              ],
            ),
          ),
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.all(12),
              itemCount: 4,
              itemBuilder: (context, index) {
                final transactions = [
                  {'title': 'Grocery Store', 'amount': '-45.20', 'icon': Icons.restaurant, 'color': Colors.pink},
                  {'title': 'Gas Station', 'amount': '-32.50', 'icon': Icons.directions_car, 'color': Colors.cyan},
                  {'title': 'Online Shopping', 'amount': '-89.99', 'icon': Icons.shopping_bag, 'color': Colors.yellow},
                  {'title': 'Electricity Bill', 'amount': '-125.00', 'icon': Icons.receipt, 'color': Colors.green},
                ];
                return _buildTransactionItem(
                  transactions[index]['title'] as String,
                  transactions[index]['amount'] as String,
                  transactions[index]['icon'] as IconData,
                  transactions[index]['color'] as Color,
                  index,
                );
              },
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        child: Icon(Icons.add, color: Colors.white),
        backgroundColor: Colors.pinkAccent,
        shape: CircleBorder(),
      ),
      floatingActionButtonLocation: FloatingActionButtonLocation.startFloat,
    );
  }

  Widget _buildCategoryCard(String title, IconData icon, Color color) {
    return Container(
      decoration: BoxDecoration(
        color: color.withOpacity(0.2),
        shape: BoxShape.circle,
        boxShadow: [BoxShadow(color: color.withOpacity(0.3), blurRadius: 8)],
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Icon(icon, color: color, size: 28),
          SizedBox(height: 6),
          Text(title, style: TextStyle(fontWeight: FontWeight.w600, color: color, fontSize: 14)),
        ],
      ),
    );
  }

  Widget _buildTransactionItem(String title, String amount, IconData icon, Color color, int index) {
    return Container(
      padding: EdgeInsets.symmetric(vertical: 8, horizontal: 12),
      color: index % 2 == 0 ? Colors.grey[900] : Colors.grey[850],
      child: Row(
        children: [
          Icon(icon, color: color, size: 20),
          SizedBox(width: 8),
          Expanded(child: Text(title, style: TextStyle(color: Colors.white, fontSize: 14))),
          Text(amount, style: TextStyle(color: Colors.redAccent, fontSize: 14, fontWeight: FontWeight.bold)),
        ],
      ),
    );
  }
}
