import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isDarkMode = false;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.pink[100],
      body: Center(
        child: Container(
          decoration: BoxDecoration(
            color: Colors.white,
            borderRadius: BorderRadius.circular(16),
            boxShadow: [BoxShadow(color: Colors.pink[200]!, blurRadius: 10)],
          ),
          padding: EdgeInsets.all(24),
          child: Column(
            mainAxisSize: MainAxisSize.min,
            children: [
              Text(
                'Light',
                style: TextStyle(
                  fontSize: 18,
                  fontFamily: 'Pacifico',
                  color: Colors.yellow,
                ),
              ),
              SizedBox(height: 16),
              AnimatedCrossFade(
                firstChild: Icon(Icons.star, color: Colors.yellow, size: 30),
                secondChild: Icon(Icons.star, color: Colors.blue, size: 30),
                crossFadeState: isDarkMode ? CrossFadeState.showSecond : CrossFadeState.showFirst,
                duration: Duration(milliseconds: 300),
              ),
              SizedBox(height: 16),
              AnimatedContainer(
                duration: Duration(milliseconds: 300),
                curve: Curves.bounceOut,
                width: 80,
                height: 40,
                decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(20),
                  color: isDarkMode ? Colors.blue : Colors.yellow,
                ),
                child: Stack(
                  children: [
                    AnimatedAlign(
                      duration: Duration(milliseconds: 300),
                      alignment: isDarkMode ? Alignment.centerRight : Alignment.centerLeft,
                      child: Container(
                        width: 36,
                        height: 36,
                        margin: EdgeInsets.all(2),
                        decoration: BoxDecoration(
                          shape: BoxShape.circle,
                          color: Colors.white,
                        ),
                      ),
                    ),
                  ],
                ),
              ),
              SizedBox(height: 16),
              Text(
                'Dark',
                style: TextStyle(
                  fontSize: 18,
                  fontFamily: 'Pacifico',
                  color: Colors.blue,
                ),
              ),
              SizedBox(height: 16),
              GestureDetector(
                onTap: () {
                  setState(() {
                    isDarkMode = !isDarkMode;
                  });
                },
                child: Container(
                  padding: EdgeInsets.symmetric(horizontal: 20, vertical: 10),
                  decoration: BoxDecoration(
                    color: Colors.pink,
                    borderRadius: BorderRadius.circular(8),
                  ),
                  child: Text(
                    'Toggle',
                    style: TextStyle(color: Colors.white, fontSize: 16),
                  ),
                ),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
