import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.green.shade50,
      body: SafeArea(
        child: Column(
          children: [
            AppBar(
              title: Text('Achievements', style: TextStyle(color: Colors.green.shade900, fontWeight: FontWeight.bold)),
              backgroundColor: Colors.white,
              centerTitle: true,
            ),
            Container(
              margin: EdgeInsets.all(20),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Container(
                    width: 200,
                    height: 10,
                    child: LinearProgressIndicator(
                      value: 0.85,
                      backgroundColor: Colors.grey.shade200,
                      valueColor: AlwaysStoppedAnimation<Color>(Colors.green),
                    ),
                  ),
                  SizedBox(width: 10),
                  Text('17/20', style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.green.shade900)),
                ],
              ),
            ),
            Container(
              height: 40,
              child: ListView(
                scrollDirection: Axis.horizontal,
                padding: EdgeInsets.symmetric(horizontal: 20),
                children: ['All', 'Gaming', 'Learning', 'Social'].map((category) => 
                  Container(
                    margin: EdgeInsets.only(right: 10),
                    child: Chip(
                      label: Text(category, style: TextStyle(color: Colors.white)),
                      backgroundColor: Colors.green.shade600,
                    ),
                  ),
                ).toList(),
              ),
            ),
            Expanded(
              child: GridView.builder(
                padding: EdgeInsets.all(20),
                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                  crossAxisCount: 3,
                  crossAxisSpacing: 10,
                  mainAxisSpacing: 10,
                  childAspectRatio: 1.0,
                ),
                itemCount: 9,
                itemBuilder: (context, index) {
                  bool isCompleted = index < 6;
                  return Container(
                    color: Colors.white,
                    child: Opacity(
                      opacity: isCompleted ? 1.0 : 0.6,
                      child: Padding(
                        padding: EdgeInsets.all(10),
                        child: Column(
                          mainAxisAlignment: MainAxisAlignment.center,
                          children: [
                            CircleAvatar(
                              radius: 20,
                              backgroundColor: isCompleted ? (index < 2 ? Colors.amber : index < 4 ? Colors.grey.shade400 : Colors.orange.shade700) : Colors.grey.shade300,
                              child: Icon(isCompleted ? Icons.star : Icons.lock, color: Colors.white, size: 20),
                            ),
                            SizedBox(height: 5),
                            Text(
                              ['First Win', 'Quick Learner', 'Team Star', 'Socialite', 'Master', 'Explorer', 'Champion', 'Legend', 'Pioneer'][index],
                              style: TextStyle(fontWeight: FontWeight.bold, fontSize: 12, color: Colors.green.shade900),
                              textAlign: TextAlign.center,
                            ),
                          ],
                        ),
                      ),
                    ),
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
}
