import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> cartItems = [
    {'name': 'VR Headset', 'price': 399.99, 'quantity': 1, 'icon': Icons.videogame_asset},
    {'name': 'Smart Glasses', 'price': 249.99, 'quantity': 1, 'icon': Icons.glasses},
  ];

  @override
  Widget build(BuildContext context) {
    double subtotal = cartItems.fold(0, (sum, item) => sum + (item['price'] * item['quantity']));
    double tax = subtotal * 0.08;
    double total = subtotal + tax;

    return Scaffold(
      appBar: AppBar(
        title: Text('Cart', style: TextStyle(fontFamily: 'Orbitron')),
        backgroundColor: Colors.grey[900],
        foregroundColor: Colors.cyanAccent,
      ),
      backgroundColor: Colors.grey[900],
      body: Column(
        children: [
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.all(16),
              itemCount: cartItems.length,
              itemBuilder: (context, index) {
                final item = cartItems[index];
                return Card(
                  margin: EdgeInsets.only(bottom: 12),
                  color: Colors.transparent,
                  elevation: 0,
                  shape: RoundedRectangleBorder(
                    side: BorderSide(color: Colors.cyanAccent, width: 1),
                    borderRadius: BorderRadius.circular(8),
                  ),
                  child: Padding(
                    padding: EdgeInsets.all(16),
                    child: Row(
                      children: [
                        Icon(item['icon'], size: 40, color: Colors.cyanAccent),
                        SizedBox(width: 16),
                        Expanded(
                          child: Column(
                            crossAxisAlignment: CrossAxisAlignment.start,
                            children: [
                              Text(item['name'], style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.white, fontFamily: 'Orbitron')),
                              Text('\$${item['price'].toStringAsFixed(2)}', style: TextStyle(fontSize: 14, color: Colors.greenAccent, fontFamily: 'Orbitron')),
                            ],
                          ),
                        ),
                        Row(
                          children: [
                            IconButton(
                              onPressed: () {
                                setState(() {
                                  if (item['quantity'] > 1) item['quantity']--;
                                });
                              },
                              icon: Icon(Icons.remove_circle_outline, color: Colors.cyanAccent),
                            ),
                            Text('${item['quantity']}', style: TextStyle(color: Colors.white, fontFamily: 'Orbitron')),
                            IconButton(
                              onPressed: () {
                                setState(() {
                                  item['quantity']++;
                                });
                              },
                              icon: Icon(Icons.add_circle_outline, color: Colors.cyanAccent),
                            ),
                          ],
                        ),
                      ],
                    ),
                  ),
                );
              },
            ),
          ),
          Card(
            margin: EdgeInsets.all(16),
            elevation: 8,
            shape: RoundedRectangleBorder(
              side: BorderSide(color: Colors.cyanAccent, width: 2),
              borderRadius: BorderRadius.circular(8),
            ),
            color: Colors.grey[800],
            child: Padding(
              padding: EdgeInsets.all(16),
              child: Column(
                children: [
                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: [
                      Text('Subtotal:', style: TextStyle(fontSize: 16, color: Colors.white, fontFamily: 'Orbitron')),
                      Text('\$${subtotal.toStringAsFixed(2)}', style: TextStyle(fontSize: 16, color: Colors.white, fontFamily: 'Orbitron')),
                    ],
                  ),
                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: [
                      Text('Tax:', style: TextStyle(fontSize: 16, color: Colors.white, fontFamily: 'Orbitron')),
                      Text('\$${tax.toStringAsFixed(2)}', style: TextStyle(fontSize: 16, color: Colors.white, fontFamily: 'Orbitron')),
                    ],
                  ),
                  Divider(color: Colors.cyanAccent),
                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: [
                      Text('Total:', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white, fontFamily: 'Orbitron')),
                      Text('\$${total.toStringAsFixed(2)}', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white, fontFamily: 'Orbitron')),
                    ],
                  ),
                ],
              ),
            ),
          ),
          Padding(
            padding: EdgeInsets.symmetric(horizontal: 16, vertical: 8),
            child: SizedBox(
              width: double.infinity,
              child: ElevatedButton(
                onPressed: () {},
                style: ElevatedButton.styleFrom(
                  backgroundColor: Colors.cyanAccent,
                  padding: EdgeInsets.symmetric(vertical: 16),
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.zero),
                ),
                child: Text('Checkout', style: TextStyle(fontSize: 16, color: Colors.black, fontFamily: 'Orbitron')),
              ),
            ),
          ),
        ],
      ),
    );
  }
}
