  Widget _buildVerticalStep(String label, bool isCompleted, bool isCurrent) {
    return Row(
      children: [
        Container(
          width: 32,
          height: 32,
          decoration: BoxDecoration(
            color: isCompleted ? Colors.purple : (isCurrent ? Colors.purple : Colors.grey[700]),
            shape: BoxShape.circle,
            boxShadow: isCompleted || isCurrent ? [BoxShadow(color: Colors.purple.withOpacity(0.3), blurRadius: 8)] : [],
          ),
          child: isCompleted
              ? Icon(Icons.check, color: Colors.white, size: 18)
              : (isCurrent ? Icon(Icons.local_shipping, color: Colors.white, size: 14) : Icon(Icons.circle, color: Colors.grey[600], size: 8)),
        ),
        SizedBox(width: 12),
        Text(label, style: TextStyle(fontSize: 14, color: isCompleted || isCurrent ? Colors.white : Colors.grey[500])),
      ],
    );
  }

  Widget _buildDarkInfoSection(String title, List<String> items) {
    return Container(
      width: double.infinity,
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Colors.grey[900],
        borderRadius: BorderRadius.circular(12),
        border: Border.all(color: Colors.grey[800]!),
