import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool showAnswer = false;
  int currentCard = 1;
  int totalCards = 10;
  bool isFavorite = false;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFF5E8C7),
      body: Column(
        children: [
          AppBar(
            backgroundColor: Color(0xFFF5E8C7),
            elevation: 0,
            title: Text('Literature Quotes', style: TextStyle(fontFamily: 'SpecialElite', color: Colors.black87, fontSize: 24)),
            actions: [
              IconButton(icon: Icon(Icons.settings, color: Colors.red[700]), onPressed: () {}),
              IconButton(icon: Icon(Icons.menu, color: Colors.red[700]), onPressed: () {}),
            ],
          ),
          Padding(
            padding: EdgeInsets.all(16),
            child: Text('Card $currentCard/$totalCards', style: TextStyle(fontFamily: 'Courier', fontSize: 18, fontWeight: FontWeight.bold, color: Colors.black87)),
          ),
          Expanded(
            child: Center(
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  IconButton(
                    icon: Icon(Icons.arrow_back, size: 40, color: Colors.black87),
                    onPressed: () {},
                  ),
                  Transform.rotate(
                    angle: 0.05,
                    child: Container(
                      width: 280,
                      height: 200,
                      decoration: BoxDecoration(
                        color: Colors.white,
                        border: Border.all(color: Colors.black87, width: 4),
                        boxShadow: [BoxShadow(color: Colors.black26, blurRadius: 8, offset: Offset(2, 2))],
                      ),
                      child: Center(
                        child: Padding(
                          padding: EdgeInsets.all(20),
                          child: Text(
                            showAnswer ? 'To be or not to be' : 'Who said this famous quote?',
                            style: TextStyle(fontFamily: 'Courier', fontSize: 18, color: Colors.black87),
                            textAlign: TextAlign.center,
                          ),
                        ),
                      ),
                    ),
                  ),
                  IconButton(
                    icon: Icon(Icons.arrow_forward, size: 40, color: Colors.black87),
                    onPressed: () {},
                  ),
                ],
              ),
            ),
          ),
          Padding(
            padding: EdgeInsets.symmetric(vertical: 20),
            child: ElevatedButton(
              onPressed: () {
                setState(() {
                  showAnswer = !showAnswer;
                });
              },
              style: ElevatedButton.styleFrom(
                backgroundColor: Colors.red[700],
                padding: EdgeInsets.symmetric(horizontal: 40, vertical: 12),
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                shadowColor: Colors.black26,
                elevation: 5,
              ),
              child: Text('Flip', style: TextStyle(color: Colors.white, fontSize: 16)),
