import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool showAnswer = false;
  int currentCard = 1;
  int totalCards = 10;
  bool isFavorite = false;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF1B263B),
      body: Column(
        children: [
          AppBar(
            backgroundColor: Color(0xFF1B263B),
            elevation: 0,
            title: Text('Art History', style: TextStyle(fontFamily: 'GreatVibes', color: Colors.amber[300], fontSize: 24)),
            actions: [
              IconButton(icon: Icon(Icons.settings, color: Colors.amber[300]), onPressed: () {}),
              IconButton(icon: Icon(Icons.menu, color: Colors.amber[300]), onPressed: () {}),
            ],
          ),
          Padding(
            padding: EdgeInsets.all(16),
            child: Column(
              children: [
                Text('$currentCard of $totalCards', style: TextStyle(fontSize: 16, color: Colors.amber[300], fontFamily: 'Lora')),
                SizedBox(height: 8),
                LinearProgressIndicator(
                  value: currentCard / totalCards,
                  backgroundColor: Colors.grey[700],
                  valueColor: AlwaysStoppedAnimation<Color>(Colors.amber[300]!),
                ),
              ],
            ),
          ),
          Expanded(
            child: Center(
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  IconButton(
                    icon: Icon(Icons.chevron_left, size: 40, color: Colors.amber[300]),
                    onPressed: () {},
                  ),
                  Container(
                    width: 300,
                    height: 200,
                    decoration: BoxDecoration(
                      color: Color(0xFFFFF3E0),
                      borderRadius: BorderRadius.circular(12),
                      border: Border.all(color: Colors.amber[300]!, width: 2),
                      boxShadow: [BoxShadow(color: Colors.black12, blurRadius: 8)],
                    ),
                    child: Center(
                      child: Padding(
                        padding: EdgeInsets.all(20),
                        child: Text(
                          showAnswer ? 'Mona Lisa' : 'Who painted the famous smile?',
                          style: TextStyle(fontSize: 18, fontFamily: 'Lora', color: Colors.black87),
                          textAlign: TextAlign.center,
                        ),
                      ),
                    ),
                  ),
                  IconButton(
                    icon: Icon(Icons.chevron_right, size: 40, color: Colors.amber[300]),
                    onPressed: () {},
                  ),
                ],
              ),
            ),
          ),
          Padding(
            padding: EdgeInsets.symmetric(vertical: 20),
            child: ElevatedButton(
              onPressed: () {
                setState(() {
                  showAnswer = !showAnswer;
                });
              },
              style: ElevatedButton.styleFrom(
                backgroundColor: Colors.amber[300],
                padding: EdgeInsets.symmetric(horizontal: 50, vertical: 12),
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
              ),
              child: Text('Flip', style: TextStyle(color: Colors.black87, fontSize: 16)),
            ),
          ),
          Container(
            padding: EdgeInsets.all(16),
            child: Row(
              mainAxisAlignment: MainAxisAlignment.spaceEvenly,
              children: [
                OutlinedButton(
                  style: OutlinedButton.styleFrom(
                    side: BorderSide(color: Colors.amber[300]!),
                    backgroundColor: Color(0xFFFFF3E0),
                    shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                  ),
                  onPressed: () {},
                  child: Text('Shuffle', style: TextStyle(color: Colors.black87)),
                ),
                OutlinedButton(
                  style: OutlinedButton.styleFrom(
                    side: BorderSide(color: Colors.amber[300]!),
                    backgroundColor: Color(0xFFFFF3E0),
                    shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                  ),
                  onPressed: () {},
                  child: Text('Restart', style: TextStyle(color: Colors.black87)),
                ),
                OutlinedButton(
                  style: OutlinedButton.styleFrom(
                    side: BorderSide(color: Colors.amber[300]!),
                    backgroundColor: Color(0xFFFFF3E0),
                    shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                  ),
                  onPressed: () {
                    setState(() {
                      isFavorite = !isFavorite;
                    });
                  },
                  child: Text(isFavorite ? 'Unfavorite' : 'Favorite', style: TextStyle(color: Colors.black87)),
                ),
              ],
            ),
          ),
        ],
      ),
    );
  }
}
