import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> with SingleTickerProviderStateMixin {
  late AnimationController _controller;

  @override
  void initState() {
    super.initState();
    _controller = AnimationController(
      duration: Duration(seconds: 2),
      vsync: this,
    )..repeat();
  }

  @override
  void dispose() {
    _controller.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFF8F8F8),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Stack(
              alignment: Alignment.center,
              children: [
                CircularProgressIndicator(
                  valueColor: AlwaysStoppedAnimation<Color>(Color(0xFFE53935)),
                ),
                AnimatedBuilder(
                  animation: _controller,
                  builder: (context, child) {
                    return Transform.rotate(
                      angle: _controller.value * 2 * 3.14159,
                      child: Stack(
                        children: [
                          Positioned(
                            top: 0,
                            child: Container(width: 8, height: 8, decoration: BoxDecoration(color: Color(0xFF757575), shape: BoxShape.circle)),
                          ),
                          Positioned(
                            top: 20 * (1 - _controller.value),
                            left: 20,
                            child: Container(width: 8, height: 8, decoration: BoxDecoration(color: Color(0xFF757575), shape: BoxShape.circle)),
                          ),
                          Positioned(
                            top: 20 * _controller.value,
                            right: 20,
                            child: Container(width: 8, height: 8, decoration: BoxDecoration(color: Color(0xFF757575), shape: BoxShape.circle)),
                          ),
                        ],
                      ),
                    );
                  },
                ),
              ],
            ),
            SizedBox(height: 24),
            Text(
              'Please Hold...',
              style: TextStyle(
                fontSize: 18,
                color: Color(0xFF424242),
                fontStyle: FontStyle.italic,
              ),
            ),
          ],
        ),
      ),
    );
  }
}
