import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool showAnswer = false;
  int currentCard = 1;
  int totalCards = 10;
  bool isFavorite = false;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.yellow[200],
      body: Container(
        decoration: BoxDecoration(
          image: DecorationImage(
            image: AssetImage('assets/clouds.png'), // Note: Asset image not included
            fit: BoxFit.cover,
            opacity: 0.2,
          ),
        ),
        child: Column(
          children: [
            AppBar(
              backgroundColor: Colors.transparent,
              elevation: 0,
              title: Text('Animal Facts', style: TextStyle(fontFamily: 'BubblegumSans', color: Colors.purple[700], fontSize: 24)),
              actions: [
                IconButton(icon: Icon(Icons.settings, color: Colors.red[600]), onPressed: () {}),
                IconButton(icon: Icon(Icons.menu, color: Colors.red[600]), onPressed: () {}),
              ],
            ),
            Padding(
              padding: EdgeInsets.all(16),
              child: Column(
                children: [
                  Text('$currentCard of $totalCards', style: TextStyle(fontSize: 16, color: Colors.purple[700], fontFamily: 'BubblegumSans')),
                  Row(
                    mainAxisAlignment: MainAxisAlignment.center,
                    children: List.generate(10, (index) => Icon(
                      Icons.star,
                      color: index < currentCard ? Colors.yellow[700] : Colors.grey[400],
                      size: 20,
                    )),
                  ),
                ],
              ),
            ),
            Expanded(
              child: Center(
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                  children: [
                    IconButton(
                      icon: Icon(Icons.arrow_back, size: 40, color: Colors.blue[600]),
                      onPressed: () {},
                    ),
                    Container(
                      width: 280,
                      height: 200,
                      decoration: BoxDecoration(
                        color: Colors.pink[100],
                        borderRadius: BorderRadius.circular(16),
                        border: Border.all(color: Colors.rainbow, width: 3), // Note: Custom rainbow border not directly supported
                        boxShadow: [BoxShadow(color: Colors.black12, blurRadius: 8)],
                      ),
                      child: Center(
                        child: Padding(
                          padding: EdgeInsets.all(20),
                          child: Text(
                            showAnswer ? 'Elephant' : 'What animal has a long trunk?',
                            style: TextStyle(fontSize: 18, color: Colors.purple[700], fontWeight: FontWeight.bold),
                            textAlign: TextAlign.center,
                          ),
                        ),
                      ),
                    ),
                    IconButton(
                      icon: Icon(Icons.arrow_forward, size: 40, color: Colors.blue[600]),
                      onPressed: () {},
                    ),
                  ],
                ),
              ),
            ),
            Padding(
              padding: EdgeInsets.symmetric(vertical: 20),
              child: ElevatedButton(
                onPressed: () {
                  setState(() {
                    showAnswer = !showAnswer;
                  });
                },
                style: ElevatedButton.styleFrom(
                  backgroundColor: Colors.yellow[700],
                  padding: EdgeInsets.symmetric(horizontal: 40, vertical: 12),
                  shape: StarBorder(), // Note: Star shape requires custom clipper
                ),
                child: Text('😊 Flip', style: TextStyle(color: Colors.white, fontSize: 16)),
              ),
            ),
            Container(
              padding: EdgeInsets.all(16),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  FloatingActionButton(
                    backgroundColor: Colors.blue[600],
                    child: Text('🐘', style: TextStyle(fontSize: 24)),
                    onPressed: () {},
                  ),
                  FloatingActionButton(
                    backgroundColor: Colors.blue[600],
                    child: Text('🦒', style: TextStyle(fontSize: 24)),
                    onPressed: () {},
                  ),
                  FloatingActionButton(
                    backgroundColor: Colors.blue[600],
                    child: Text(isFavorite ? '❤️' : '🤍', style: TextStyle(fontSize: 24)),
                    onPressed: () {
                      setState(() {
                        isFavorite = !isFavorite;
                      });
                    },
                  ),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}
