import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String pin = '';
  final int pinLength = 4;

  void _onNumberPressed(String number) {
    if (pin.length < pinLength) {
      setState(() {
        pin += number;
      });
    }
  }

  void _onDeletePressed() {
    if (pin.isNotEmpty) {
      setState(() {
        pin = pin.substring(0, pin.length - 1);
      });
    }
  }

  Widget _buildPinSquare(bool filled) {
    return Container(
      width: 55,
      height: 55,
      decoration: BoxDecoration(
        shape: BoxShape.rectangle,
        border: Border.all(color: Colors.white, width: 2),
        color: filled ? Colors.teal : Colors.transparent,
      ),
      child: filled ? Icon(Icons.square, color: Colors.teal, size: 20) : null,
    );
  }

  Widget _buildKeypadButton(String text, {VoidCallback? onPressed}) {
    return Container(
      width: 65,
      height: 65,
      child: ElevatedButton(
        onPressed: onPressed,
        style: ElevatedButton.styleFrom(
          backgroundColor: Colors.grey[800],
          foregroundColor: Colors.white,
          shape: CircleBorder(),
          elevation: 2,
        ),
        child: Text(text, style: TextStyle(fontSize: 22, fontWeight: FontWeight.w500)),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      body: Padding(
        padding: EdgeInsets.all(25),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Icon(Icons.lock, size: 90, color: Colors.teal),
            SizedBox(height: 30),
            Text('Secure Access', style: TextStyle(fontSize: 30, fontWeight: FontWeight.bold, color: Colors.white)),
            SizedBox(height: 15),
            Text('Input PIN to Proceed', style: TextStyle(fontSize: 18, color: Colors.grey[400])),
            SizedBox(height: 30),
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: List.generate(pinLength, (index) => Padding(
                padding: EdgeInsets.symmetric(horizontal: 12),
                child: _buildPinSquare(index < pin.length),
              )),
            ),
            SizedBox(height: 30),
            GridView.builder(
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 3,
                crossAxisSpacing: 20,
                mainAxisSpacing: 20,
                childAspectRatio: 1,
              ),
              itemCount: 12,
              itemBuilder: (context, index) {
                if (index < 9) {
                  String number = (index + 1).toString();
                  return _buildKeypadButton(number, onPressed: () => _onNumberPressed(number));
                } else if (index == 9) {
                  return Container();
                } else if (index == 10) {
                  return _buildKeypadButton('0', onPressed: () => _onNumberPressed('0'));
                } else {
                  return _buildKeypadButton('âŒ«', onPressed: _onDeletePressed);
                }
              },
            ),
          ],
        ),
      ),
    );
  }
}
