class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String? selectedLanguage;

  final List<Map<String, String>> languages = [
    {'code': 'en', 'native': 'English', 'english': 'English', 'flag': '🇺🇸'},
    {'code': 'es', 'native': 'Español', 'english': 'Spanish', 'flag': '🇪🇸'},
    {'code': 'fr', 'native': 'Français', 'english': 'French', 'flag': '🇫🇷'},
    {'code': 'de', 'native': 'Deutsch', 'english': 'German', 'flag': '🇩🇪'},
    {'code': 'zh', 'native': '中文', 'english': 'Chinese', 'flag': '🇨🇳'},
    {'code': 'ja', 'native': '日本語', 'english': 'Japanese', 'flag': '🇯🇵'},
    {'code': 'ar', 'native': 'العربية', 'english': 'Arabic', 'flag': '🇸🇦'},
    {'code': 'ru', 'native': 'Русский', 'english': 'Russian', 'flag': '🇷🇺'},
    {'code': 'pt', 'native': 'Português', 'english': 'Portuguese', 'flag': '🇵🇹'},
    {'code': 'it', 'native': 'Italiano', 'english': 'Italian', 'flag': '🇮🇹'},
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFFAFAFA),
      body: SafeArea(
        child: Padding(
          padding: EdgeInsets.symmetric(horizontal: 32, vertical: 40),
          child: Column(
            children: [
              SizedBox(height: 60),
              Container(
                width: 60,
                height: 60,
                decoration: BoxDecoration(
                  color: Color(0xFFE8F5E8),
                  shape: BoxShape.circle,
                ),
                child: Icon(
                  Icons.language_outlined,
                  color: Color(0xFF81C784),
                  size: 30,
                ),
              ),
              SizedBox(height: 40),
              Text(
                'Pick a Language',
                style: TextStyle(
                  fontSize: 32,
                  fontWeight: FontWeight.w300,
                  color: Color(0xFF2E2E2E),
                  letterSpacing: 0.5,
                ),
                textAlign: TextAlign.center,
              ),
              SizedBox(height: 60),
              Expanded(
                child: ListView.separated(
                  itemCount: languages.length,
                  separatorBuilder: (context, index) => Divider(
                    color: Color(0xFFE0E0E0),
                    height: 1,
                  ),
                  itemBuilder: (context, index) {
                    final lang = languages[index];
                    final isSelected = selectedLanguage == lang['code'];
                    return GestureDetector(
                      onTap: () => setState(() => selectedLanguage = lang['code']),
                      child: Container(
                        padding: EdgeInsets.symmetric(vertical: 20, horizontal: 16),
                        decoration: BoxDecoration(
                          color: isSelected ? Color(0xFFF8E6F0) : Colors.transparent,
                          borderRadius: BorderRadius.circular(8),
                        ),
                        child: Row(
                          children: [
                            Text(
                              lang['flag']!,
                              style: TextStyle(fontSize: 28),
                            ),
                            SizedBox(width: 20),
                            Expanded(
                              child: Column(
                                crossAxisAlignment: CrossAxisAlignment.start,
                                children: [
                                  Text(
                                    lang['native']!,
                                    style: TextStyle(
                                      fontSize: 18,
                                      fontWeight: FontWeight.w500,
                                      color: Color(0xFF2E2E2E),
                                    ),
                                  ),
                                  SizedBox(height: 4),
                                  Text(
                                    lang['english']!,
                                    style: TextStyle(
                                      fontSize: 14,
                                      color: Color(0xFF757575),
                                      fontWeight: FontWeight.w400,
                                    ),
                                  ),
                                ],
                              ),
                            ),
                            if (isSelected)
                              Container(
                                width: 20,
                                height: 20,
                                decoration: BoxDecoration(
                                  color: Color(0xFFE91E63),
                                  shape: BoxShape.circle,
                                ),
                                child: Icon(
                                  Icons.check,
                                  color: Colors.white,
                                  size: 14,
                                ),
                              ),
                          ],
                        ),
                      ),
                    );
                  },
                ),
              ),
              SizedBox(height: 40),
              Container(
                width: double.infinity,
                height: 56,
                decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(28),
                  boxShadow: selectedLanguage != null ? [
                    BoxShadow(
                      color: Color(0xFFE91E63).withOpacity(0.3),
                      blurRadius: 15,
                      offset: Offset(0, 5),
                    ),
                  ] : null,
                ),
                child: ElevatedButton(
                  onPressed: selectedLanguage != null ? () {} : null,
                  style: ElevatedButton.styleFrom(
                    backgroundColor: selectedLanguage != null ? Color(0xFFE91E63) : Color(0xFFE0E0E0),
                    foregroundColor: selectedLanguage != null ? Colors.white : Color(0xFF9E9E9E),
                    elevation: 0,
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(28),
                    ),
                  ),
                  child: Text(
                    'Continue',
                    style: TextStyle(
                      fontSize: 16,
                      fontWeight: FontWeight.w500,
                      letterSpacing: 0.5,
                    ),
                  ),
                ),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
