import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF1C2526),
      appBar: AppBar(
        backgroundColor: Color(0xFF1C2526),
        elevation: 0,
        title: Text(
          'Categories',
          style: TextStyle(
            color: Colors.amber[600],
            fontSize: 24,
            fontWeight: FontWeight.bold,
            fontFamily: 'Lora',
          ),
        ),
        centerTitle: true,
      ),
      body: Padding(
        padding: EdgeInsets.symmetric(vertical: 16.0),
        child: SingleChildScrollView(
          scrollDirection: Axis.horizontal,
          child: Row(
            children: [
              _buildCategoryCard(Icons.shopping_bag, 'Electronics', Colors.amber[600]),
              _buildCategoryCard(Icons.headphones, 'Audio', Colors.amber[600]),
              _buildCategoryCard(Icons.laptop, 'Computers', Colors.amber[600]),
              _buildCategoryCard(Icons.checkroom, 'Fashion', Colors.amber[600]),
              _buildCategoryCard(Icons.book, 'Books', Colors.amber[600]),
              _buildCategoryCard(Icons.home, 'Home', Colors.amber[600]),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildCategoryCard(IconData icon, String title, Color iconColor) {
    return Container(
      width: 150,
      margin: EdgeInsets.symmetric(horizontal: 8.0),
      padding: EdgeInsets.all(16.0),
      decoration: BoxDecoration(
        color: Colors.grey[800],
        border: Border.all(color: Colors.amber[600]!, width: 1),
        borderRadius: BorderRadius.circular(12),
        boxShadow: [
          BoxShadow(
            color: Colors.black26,
            blurRadius: 4,
            offset: Offset(0, 2),
          ),
        ],
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Icon(
            icon,
            size: 40,
            color: iconColor,
          ),
          SizedBox(height: 12),
          Text(
            title,
            style: TextStyle(
              fontSize: 16,
              fontWeight: FontWeight.w500,
              color: Colors.white,
              fontFamily: 'Lora',
            ),
          ),
        ],
      ),
    );
  }
}
