import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        backgroundColor: Colors.white,
        elevation: 0,
        leading: Icon(Icons.menu, color: Colors.blue[900]),
        title: Text('Dashboard', style: TextStyle(color: Colors.blue[900], fontWeight: FontWeight.w600)),
        actions: [
          Icon(Icons.search, color: Colors.blue[900]),
          SizedBox(width: 16),
          Icon(Icons.notifications_outlined, color: Colors.blue[900]),
          SizedBox(width: 16),
        ],
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(20),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Welcome, Sarah!', style: TextStyle(fontSize: 26, fontWeight: FontWeight.bold, color: Colors.black87)),
            SizedBox(height: 24),
            GridView.count(
              crossAxisCount: 2,
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              crossAxisSpacing: 16,
              mainAxisSpacing: 16,
              children: [
                _buildMetricCard('2,345', 'Total Orders', Colors.blue[100]!, Colors.blue[900]!, Icons.trending_up),
                _buildMetricCard('789', 'New Clients', Colors.teal[100]!, Colors.teal[900]!, Icons.person_add),
                _buildMetricCard('92%', 'Satisfaction', Colors.amber[100]!, Colors.amber[900]!, Icons.star),
                _buildMetricCard('\$15K', 'Revenue', Colors.red[100]!, Colors.red[900]!, Icons.monetization_on),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Activity', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.black87)),
            SizedBox(height: 12),
            _buildActivityItem(Icons.shopping_cart, 'Order Placed', 'Order #5678 confirmed', '1 min ago'),
            _buildActivityItem(Icons.person, 'User Joined', 'Jane Doe registered', '3 min ago'),
            _buildActivityItem(Icons.payment, 'Payment', '\$499.99 received', '8 min ago'),
          ],
        ),
      ),
    );
  }

  Widget _buildMetricCard(String value, String label, Color bgColor, Color iconColor, IconData icon) {
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: bgColor,
        borderRadius: BorderRadius.circular(12),
        boxShadow: [BoxShadow(color: Colors.grey[300]!, blurRadius: 8, offset: Offset(0, 2))],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              Icon(icon, color: iconColor, size: 24),
              Icon(Icons.more_vert, color: Colors.grey[600], size: 16),
            ],
          ),
          Spacer(),
          Text(value, style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Colors.black87)),
          Text(label, style: TextStyle(fontSize: 12, color: Colors.grey[600])),
        ],
      ),
    );
  }

  Widget _buildActivityItem(IconData icon, String title, String subtitle, String time) {
    return Container(
      margin: EdgeInsets.only(bottom: 12),
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: Colors.grey[100],
        borderRadius: BorderRadius.circular(8),
      ),
      child: Row(
        children: [
          CircleAvatar(
            radius: 20,
            backgroundColor: Colors.blue[100],
            child: Icon(icon, color: Colors.blue[900], size: 20),
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14)),
                Text(subtitle, style: TextStyle(color: Colors.grey[600], fontSize: 12)),
              ],
            ),
          ),
          Text(time, style: TextStyle(color: Colors.grey[500], fontSize: 11)),
        ],
      ),
    );
  }
}
