              children: [
                _buildMetricCard('11,234', 'Orders', Colors.red[100]!, Icons.trending_up),
                _buildMetricCard('8,901', 'Users', Colors.red[100]!, Icons.person_add),
                _buildMetricCard('94%', 'Satisfaction', Colors.red[100]!, Icons.star),
                _buildMetricCard('\$55K', 'Revenue', Colors.red[100]!, Icons.monetization_on),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Notifications', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.red[800])),
            SizedBox(height: 12),
            _buildActivityItem(Icons.shopping_cart, 'Order Alert', 'Order #0123', '1 min ago'),
            _buildActivityItem(Icons.person, 'User Alert', 'Ethan White joined', '4 min ago'),
            _buildActivityItem(Icons.payment, 'Payment Alert', '\$3,499.99', '8 min ago'),
          ],
        ),
      ),
      bottomNavigationBar: BottomNavigationBar(
        backgroundColor: Colors.white,
        selectedItemColor: Colors.red[800],
