import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> selectedFiles = [];

  void _addFile(String name, String size) {
    setState(() {
      selectedFiles.add({'name': name, 'size': size});
    });
  }

  void _removeFile(int index) {
    setState(() {
      selectedFiles.removeAt(index);
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF1A2326),
      appBar: AppBar(
        title: Text('Image Upload', style: TextStyle(color: Colors.white)),
        backgroundColor: Color(0xFF263238),
        elevation: 0,
      ),
      body: Row(
        children: [
          Container(
            width: 200,
            color: Color(0xFF1A2326),
            padding: EdgeInsets.all(16.0),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  'Selected Files',
                  style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.white),
                ),
                SizedBox(height: 12),
                Expanded(
                  child: ListView(
                    children: selectedFiles.asMap().entries.map((entry) {
                      int index = entry.key;
                      Map<String, dynamic> file = entry.value;
                      return Container(
                        margin: EdgeInsets.only(bottom: 10),
                        padding: EdgeInsets.all(12),
                        decoration: BoxDecoration(
                          border: Border.all(color: Color(0xFFAB47BC)),
                          borderRadius: BorderRadius.circular(8),
                          color: Color(0xFF263238),
                        ),
                        child: Row(
                          children: [
                            Container(
                              width: 40,
                              height: 40,
                              decoration: BoxDecoration(
                                color: Colors.grey[800],
                                borderRadius: BorderRadius.circular(4),
                              ),
                              child: Icon(Icons.image, color: Colors.grey[400]),
                            ),
                            SizedBox(width: 12),
                            Expanded(
                              child: Column(
                                crossAxisAlignment: CrossAxisAlignment.start,
                                children: [
                                  Text(
                                    file['name'],
                                    style: TextStyle(fontWeight: FontWeight.w500, color: Colors.white),
                                  ),
                                  Text(
                                    file['size'],
                                    style: TextStyle(color: Colors.grey[400], fontSize: 12),
                                  ),
                                ],
                              ),
                            ),
                            IconButton(
                              onPressed: () => _removeFile(index),
                              icon: Icon(Icons.delete, color: Color(0xFFAB47BC)),
                            ),
                          ],
                        ),
                      );
                    }).toList(),
                  ),
                ),
              ],
            ),
          ),
          Expanded(
            child: Padding(
              padding: EdgeInsets.all(16.0),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.stretch,
                children: [
                  Container(
                    height: 200,
                    decoration: BoxDecoration(
                      border: Border.all(color: Color(0xFFAB47BC), width: 2),
                      borderRadius: BorderRadius.circular(12),
                      color: Color(0xFF263238),
                    ),
                    child: InkWell(
                      onTap: () => _addFile('image_${selectedFiles.length + 1}.jpg', '2.0 MB'),
                      borderRadius: BorderRadius.circular(12),
                      child: Column(
                        mainAxisAlignment: MainAxisAlignment.center,
                        children: [
                          Icon(Icons.upload_file, size: 50, color: Color(0xFFAB47BC)),
                          SizedBox(height: 12),
                          Text(
                            'Select Images',
                            style: TextStyle(fontSize: 16, fontWeight: FontWeight.w500, color: Colors.white),
                          ),
                          SizedBox(height: 8),
                          Text(
                            'PNG, JPG up to 6MB',
                            style: TextStyle(fontSize: 12, color: Colors.grey[400]),
                          ),
                        ],
                      ),
                    ),
                  ),
                  SizedBox(height: 20),
                  ElevatedButton(
                    onPressed: selectedFiles.isNotEmpty ? () {} : null,
                    style: ElevatedButton.styleFrom(
                      backgroundColor: Color(0xFFAB47BC),
                      padding: EdgeInsets.symmetric(vertical: 14),
                      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(10)),
                    ),
                    child: Text(
                      'Start Upload',
                      style: TextStyle(fontSize: 16, fontWeight: FontWeight.w600, color: Colors.white),
                    ),
                  ),
                ],
              ),
            ),
          ),
        ],
      ),
    );
  }
}
