                            mainAxisAlignment: MainAxisAlignment.center,
                            children: [
                              Stack(
                                alignment: Alignment.center,
                                children: [
                                  CircleAvatar(
                                    radius: 30,
                                    backgroundColor: isCompleted ? (index < 2 ? Colors.amber : index < 4 ? Colors.grey.shade400 : Colors.orange.shade700) : Colors.grey.shade300,
                                    child: Icon(
                                      isCompleted ? Icons.star : Icons.lock,
                                      color: Colors.white,
                                      size: 30,
                                    ),
