                  itemBuilder: (context, index) {
                    bool isCompleted = index < 5;
                    return Container(
                      decoration: BoxDecoration(
                        color: Colors.grey.shade800,
                        borderRadius: BorderRadius.circular(15),
                        border: Border.all(color: Colors.grey.shade700),
                        boxShadow: isCompleted ? [BoxShadow(color: Colors.amber.withOpacity(0.3), blurRadius: 10, spreadRadius: 1)] : [],
                      ),
                      child: Opacity(
                        opacity: isCompleted ? 1.0 : 0.5,
                        child: Padding(
                          padding: EdgeInsets.all(15),
                          child: Column(
                            mainAxisAlignment: MainAxisAlignment.center,
                            children: [
                              Stack(
                                alignment: Alignment.center,
                                children: [
                                  Container(
                                    width: 60,
                                    height: 60,
                                    decoration: BoxDecoration(
                                      shape: BoxShape.circle,
                                      color: isCompleted ? (index < 2 ? Colors.amber : index < 4 ? Colors.grey.shade400 : Colors.orange.shade700) : Colors.grey.shade700,
                                      boxShadow: isCompleted ? [BoxShadow(color: Colors.amber.withOpacity(0.5), blurRadius: 10)] : [],
                                    ),
                                    child: Icon(
                                      isCompleted ? Icons.star : Icons.lock,
                                      color: Colors.white,
                                      size: 30,
                                    ),
                                  ),
                                  if (isCompleted)
                                    Positioned(
                                      right: 0,
                                      bottom: 0,
                                      child: CircleAvatar(
                                        radius: 10,
                                        backgroundColor: Colors.green.shade400,
                                        child: Icon(Icons.check, size: 12, color: Colors.black),
                                      ),
                                    ),
                                ],
                              ),
                              SizedBox(height: 10),
                              Text(
                                ['Warrior', 'Explorer', 'Crafter', 'Legend', 'Champion', 'Rookie', 'Novice', 'Apprentice'][index],
                                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 14, color: Colors.white),
                                textAlign: TextAlign.center,
                              ),
                              SizedBox(height: 5),
                              Text(
                                ['Win 100 battles', 'Visit 50 locations', 'Craft 25 items', 'Reach max level', 'Win tournament', 'Complete tutorial', 'Join guild', 'Learn basics'][index],
                                style: TextStyle(fontSize: 10, color: Colors.grey.shade400),
                                textAlign: TextAlign.center,
                                maxLines: 2,
                                overflow: TextOverflow.ellipsis,
                              ),
                            ],
                          ),
                        ),
                      ),
                    );
                  },
                ),
              ),
            ],
          ),
        ),
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        backgroundColor: Colors.green.shade400,
        child: Icon(Icons.add, color: Colors.black),
      ),
    );
  }
}
