import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String pin = '';
  final int pinLength = 4;

  void _onNumberPressed(String number) {
    if (pin.length < pinLength) {
      setState(() {
        pin += number;
      });
    }
  }

  void _onDeletePressed() {
    if (pin.isNotEmpty) {
      setState(() {
        pin = pin.substring(0, pin.length - 1);
      });
    }
  }

  Widget _buildPinRectangle(bool filled) {
    return Container(
      width: 70,
      height: 40,
      decoration: BoxDecoration(
        border: Border.all(color: Colors.grey[600]!, width: 2),
        borderRadius: BorderRadius.circular(8),
        color: filled ? Colors.blue[900] : Colors.transparent,
      ),
      child: filled ? Icon(Icons.rectangle, color: Colors.blue[900], size: 20) : null,
    );
  }

  Widget _buildKeypadButton(String text, {VoidCallback? onPressed}) {
    return Container(
      width: 60,
      height: 60,
      child: ElevatedButton(
        onPressed: onPressed,
        style: ElevatedButton.styleFrom(
          backgroundColor: Colors.grey[200],
          foregroundColor: Colors.black,
          shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
          elevation: 2,
        ),
        child: Text(text, style: TextStyle(fontSize: 20, fontWeight: FontWeight.w500)),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[100],
      body: Padding(
        padding: EdgeInsets.all(20),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.start,
          children: [
            SizedBox(height: 50),
            Icon(Icons.lock, size: 70, color: Colors.blue[900]),
            SizedBox(height: 20),
            Text('App Security', style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold, color: Colors.black)),
            SizedBox(height: 8),
            Text('Enter PIN', style: TextStyle(fontSize: 14, color: Colors.grey[600])),
            SizedBox(height: 20),
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: List.generate(pinLength, (index) => Padding(
                padding: EdgeInsets.symmetric(horizontal: 10),
                child: _buildPinRectangle(index < pin.length),
              )),
            ),
            SizedBox(height: 20),
            GridView.builder(
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 3,
                crossAxisSpacing: 15,
                mainAxisSpacing: 15,
                childAspectRatio: 1,
              ),
              itemCount: 12,
              itemBuilder: (context, index) {
                if (index < 9) {
                  String number = (index + 1).toString();
                  return _buildKeypadButton(number, onPressed: () => _onNumberPressed(number));
                } else if (index == 9) {
                  return Container();
                } else if (index == 10) {
                  return _buildKeypadButton('0', onPressed: () => _onNumberPressed('0'));
                } else {
                  return _buildKeypadButton('âŒ«', onPressed: _onDeletePressed);
                }
              },
            ),
          ],
        ),
      ),
    );
  }
}
