import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String pin = '';
  final int pinLength = 4;

  void _onNumberPressed(String number) {
    if (pin.length < pinLength) {
      setState(() {
        pin += number;
      });
    }
  }

  void _onDeletePressed() {
    if (pin.isNotEmpty) {
      setState(() {
        pin = pin.substring(0, pin.length - 1);
      });
    }
  }

  Widget _buildPinCircle(bool filled) {
    return Container(
      width: 60,
      height: 60,
      decoration: BoxDecoration(
        shape: BoxShape.circle,
        border: Border.all(color: Colors.blue[800]!, width: 2),
        color: filled ? Colors.blue[800] : Colors.transparent,
      ),
      child: filled ? Icon(Icons.circle, color: Colors.blue[800], size: 20) : null,
    );
  }

  Widget _buildKeypadButton(String text, {VoidCallback? onPressed}) {
    return Container(
      width: 70,
      height: 70,
      child: ElevatedButton(
        onPressed: onPressed,
        style: ElevatedButton.styleFrom(
          backgroundColor: Colors.white,
          foregroundColor: Colors.blue[800],
          shape: CircleBorder(),
          elevation: 4,
          shadowColor: Colors.black26,
        ),
        child: Text(text, style: TextStyle(fontSize: 24, fontWeight: FontWeight.w500)),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.blue[100],
      body: Padding(
        padding: EdgeInsets.all(20),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Icon(Icons.lock, size: 80, color: Colors.blue[800]),
            SizedBox(height: 30),
            Text('Secure App', style: TextStyle(fontSize: 28, fontWeight: FontWeight.bold, color: Colors.blue[800])),
            SizedBox(height: 10),
            Text('Enter 4-digit PIN', style: TextStyle(fontSize: 16, color: Colors.grey[600])),
            SizedBox(height: 30),
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: List.generate(pinLength, (index) => Padding(
                padding: EdgeInsets.symmetric(horizontal: 10),
                child: _buildPinCircle(index < pin.length),
              )),
            ),
            SizedBox(height: 30),
            GridView.builder(
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 3,
                crossAxisSpacing: 20,
                mainAxisSpacing: 20,
                childAspectRatio: 1,
              ),
              itemCount: 12,
              itemBuilder: (context, index) {
                if (index < 9) {
                  String number = (index + 1).toString();
                  return _buildKeypadButton(number, onPressed: () => _onNumberPressed(number));
                } else if (index == 9) {
                  return Container();
                } else if (index == 10) {
                  return _buildKeypadButton('0', onPressed: () => _onNumberPressed('0'));
                } else {
                  return _buildKeypadButton('âŒ«', onPressed: _onDeletePressed);
                }
              },
            ),
          ],
        ),
      ),
    );
  }
}
