import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String? selectedLanguage;

  final List<Map<String, String>> languages = [
    {'code': 'en', 'native': 'English', 'english': 'English', 'flag': '🇺🇸'},
    {'code': 'es', 'native': 'Español', 'english': 'Spanish', 'flag': '🇪🇸'},
    {'code': 'fr', 'native': 'Français', 'english': 'French', 'flag': '🇫🇷'},
    {'code': 'de', 'native': 'Deutsch', 'english': 'German', 'flag': '🇩🇪'},
    {'code': 'zh', 'native': '中文', 'english': 'Chinese', 'flag': '🇨🇳'},
    {'code': 'ja', 'native': '日本語', 'english': 'Japanese', 'flag': '🇯🇵'},
    {'code': 'ar', 'native': 'العربية', 'english': 'Arabic', 'flag': '🇸🇦'},
    {'code': 'ru', 'native': 'Русский', 'english': 'Russian', 'flag': '🇷🇺'},
    {'code': 'pt', 'native': 'Português', 'english': 'Portuguese', 'flag': '🇵🇹'},
    {'code': 'it', 'native': 'Italiano', 'english': 'Italian', 'flag': '🇮🇹'},
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(
            colors: [Colors.blue.shade200, Colors.blue.shade800],
            begin: Alignment.topCenter,
            end: Alignment.bottomCenter,
          ),
        ),
        child: SafeArea(
          child: Padding(
            padding: EdgeInsets.all(20),
            child: Column(
              children: [
                SizedBox(height: 32),
                Container(
                  width: 80,
                  height: 80,
                  decoration: BoxDecoration(
                    color: Colors.blue.shade600,
                    shape: BoxShape.circle,
                  ),
                  child: Icon(Icons.public, color: Colors.white, size: 40),
                ),
                SizedBox(height: 24),
                Text(
                  'Choose Your Language',
                  style: TextStyle(
                    fontSize: 28,
                    fontWeight: FontWeight.w600,
                    color: Colors.white,
                  ),
                  textAlign: TextAlign.center,
                ),
                SizedBox(height: 32),
                Expanded(
                  child: GridView.builder(
                    gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                      crossAxisCount: 2,
                      crossAxisSpacing: 16,
                      mainAxisSpacing: 16,
                      childAspectRatio: 1.0,
                    ),
                    itemCount: languages.length,
                    itemBuilder: (context, index) {
                      final lang = languages[index];
                      final isSelected = selectedLanguage == lang['code'];
                      return GestureDetector(
                        onTap: () => setState(() => selectedLanguage = lang['code']),
                        child: AnimatedContainer(
                          duration: Duration(milliseconds: 200),
                          decoration: BoxDecoration(
                            shape: BoxShape.circle,
                            color: Colors.white,
                            border: Border.all(
                              color: isSelected ? Colors.blue.shade400 : Colors.grey.shade300,
                              width: isSelected ? 3 : 1,
                            ),
                            boxShadow: isSelected
                                ? [BoxShadow(color: Colors.blue.shade200, blurRadius: 10)]
                                : [],
                          ),
                          child: Column(
                            mainAxisAlignment: MainAxisAlignment.center,
                            children: [
                              Text(
                                lang['flag']!,
                                style: TextStyle(fontSize: 30),
                              ),
                              SizedBox(height: 8),
                              Text(
                                lang['native']!,
                                style: TextStyle(
                                  fontSize: 14,
                                  fontWeight: FontWeight.w600,
                                  color: Colors.black87,
                                ),
                                textAlign: TextAlign.center,
                              ),
                              Text(
                                lang['english']!,
                                style: TextStyle(
                                  fontSize: 10,
                                  color: Colors.grey.shade600,
                                ),
                                textAlign: TextAlign.center,
                              ),
                            ],
                          ),
                        ),
                      );
                    },
                  ),
                ),
                SizedBox(height: 20),
                SizedBox(
                  width: double.infinity,
                  height: 50,
                  child: ElevatedButton(
                    onPressed: selectedLanguage != null ? () {} : null,
                    style: ElevatedButton.styleFrom(
                      backgroundColor: Colors.blue.shade600,
                      foregroundColor: Colors.white,
                      shape: RoundedRectangleBorder(
                        borderRadius: BorderRadius.circular(12),
                      ),
                    ),
                    child: Text(
                      'Proceed',
                      style: TextStyle(
                        fontSize: 16,
                        fontWeight: FontWeight.w600,
                      ),
                    ),
                  ),
                ),
              ],
            ),
          ),
        ),
      ),
    );
  }
}
