import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.green[100],
      appBar: AppBar(
        backgroundColor: Colors.green[100],
        elevation: 0,
        title: Text(
          'Categories',
          style: TextStyle(
            color: Colors.purple[800],
            fontSize: 24,
            fontWeight: FontWeight.bold,
            fontFamily: 'ComicNeue',
          ),
        ),
        centerTitle: true,
      ),
      body: Padding(
        padding: EdgeInsets.all(16.0),
        child: GridView.count(
          crossAxisCount: 2,
          crossAxisSpacing: 16,
          mainAxisSpacing: 16,
          childAspectRatio: 1.0,
          children: [
            _buildCategoryCard(Icons.shopping_bag, 'Electronics', Colors.purple[600]),
            _buildCategoryCard(Icons.headphones, 'Audio', Colors.purple[600]),
            _buildCategoryCard(Icons.laptop, 'Computers', Colors.purple[600]),
            _buildCategoryCard(Icons.checkroom, 'Fashion', Colors.purple[600]),
            _buildCategoryCard(Icons.book, 'Books', Colors.purple[600]),
            _buildCategoryCard(Icons.home, 'Home', Colors.purple[600]),
          ],
        ),
      ),
    );
  }

  Widget _buildCategoryCard(IconData icon, String title, Color iconColor) {
    return GestureDetector(
      onTap: () {},
      child: AnimatedContainer(
        duration: Duration(milliseconds: 200),
        curve: Curves.bounceOut,
        decoration: BoxDecoration(
          color: Colors.white,
          border: Border.all(color: Colors.purple[600]!, width: 2),
          borderRadius: BorderRadius.circular(12),
          boxShadow: [
            BoxShadow(
              color: Colors.black12,
              blurRadius: 4,
              offset: Offset(0, 2),
            ),
          ],
        ),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Icon(
              icon,
              size: 40,
              color: iconColor,
            ),
            SizedBox(height: 12),
            Text(
              title,
              style: TextStyle(
                fontSize: 16,
                fontWeight: FontWeight.w500,
                color: Colors.black,
                fontFamily: 'ComicNeue',
              ),
            ),
          ],
        ),
      ),
    );
  }
}
