import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isFollowersSelected = true;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        backgroundColor: Colors.black,
        elevation: 0,
        leading: Icon(Icons.arrow_back, color: Colors.lightGreenAccent),
        title: Text('Followers', style: TextStyle(color: Colors.lightGreenAccent, fontWeight: FontWeight.bold)),
        actions: [Icon(Icons.search, color: Colors.lightGreenAccent), SizedBox(width: 16)],
        centerTitle: true,
      ),
      body: Column(
        children: [
          Container(
            padding: EdgeInsets.all(16),
            child: Row(
              children: [
                Expanded(
                  child: GestureDetector(
                    onTap: () => setState(() => isFollowersSelected = false),
                    child: Container(
                      padding: EdgeInsets.symmetric(vertical: 12),
                      decoration: BoxDecoration(
                        color: !isFollowersSelected ? Colors.lightGreenAccent : Colors.black,
                        border: Border.all(color: Colors.lightGreenAccent),
                        borderRadius: BorderRadius.circular(25),
                        boxShadow: !isFollowersSelected ? [BoxShadow(color: Colors.lightGreenAccent.withOpacity(0.5), blurRadius: 8)] : [],
                      ),
                      child: Text(
                        'Following',
                        textAlign: TextAlign.center,
                        style: TextStyle(
                          color: !isFollowersSelected ? Colors.black : Colors.lightGreenAccent,
                          fontWeight: FontWeight.w600,
                        ),
                      ),
                    ),
                  ),
                ),
                SizedBox(width: 8),
                Expanded(
                  child: GestureDetector(
                    onTap: () => setState(() => isFollowersSelected = true),
                    child: Container(
                      padding: EdgeInsets.symmetric(vertical: 12),
                      decoration: BoxDecoration(
                        color: isFollowersSelected ? Colors.lightGreenAccent : Colors.black,
                        border: Border.all(color: Colors.lightGreenAccent),
                        borderRadius: BorderRadius.circular(25),
                        boxShadow: isFollowersSelected ? [BoxShadow(color: Colors.lightGreenAccent.withOpacity(0.5), blurRadius: 8)] : [],
                      ),
                      child: Text(
                        'Followers',
                        textAlign: TextAlign.center,
                        style: TextStyle(
                          color: isFollowersSelected ? Colors.black : Colors.lightGreenAccent,
                          fontWeight: FontWeight.w600,
                        ),
                      ),
                    ),
                  ),
                ),
              ],
            ),
          ),
          Expanded(
            child: ListView.separated(
              padding: EdgeInsets.symmetric(horizontal: 16),
              itemCount: 10,
              separatorBuilder: (context, index) => Divider(height: 1, color: Colors.lightGreenAccent.withOpacity(0.5)),
              itemBuilder: (context, index) {
                return ListTile(
                  contentPadding: EdgeInsets.symmetric(vertical: 8),
                  leading: Container(
                    width: 60,
                    height: 60,
                    decoration: BoxDecoration(
                      shape: BoxShape.circle,
                      boxShadow: [BoxShadow(color: Colors.lightGreenAccent.withOpacity(0.3), blurRadius: 8)],
                    ),
                    child: CircleAvatar(
                      backgroundColor: Colors.grey[800],
                      backgroundImage: NetworkImage('https://picsum.photos/60/60?random=$index'),
                    ),
                  ),
                  title: Text(
                    'User Name ${index + 1}',
                    style: TextStyle(fontWeight: FontWeight.bold, color: Colors.white),
                  ),
                  subtitle: Text(
                    '@username${index + 1}',
                    style: TextStyle(color: Colors.lightGreenAccent),
                  ),
                  trailing: ElevatedButton(
                    onPressed: () {},
                    style: ElevatedButton.styleFrom(
                      backgroundColor: Colors.lightGreenAccent,
                      foregroundColor: Colors.black,
                      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),
                      padding: EdgeInsets.symmetric(horizontal: 20, vertical: 8),
                    ),
                    child: Text('Follow'),
                  ),
                );
              },
            ),
          ),
        ],
      ),
    );
  }
}
