import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isRunning = false;
  int seconds = 0;
  String currentTask = 'Design Review';
  TextEditingController taskController = TextEditingController();

  String formatTime(int totalSeconds) {
    int hours = totalSeconds ~/ 3600;
    int minutes = (totalSeconds % 3600) ~/ 60;
    int secs = totalSeconds % 60;
    return '${hours.toString().padLeft(2, '0')}:${minutes.toString().padLeft(2, '0')}:${secs.toString().padLeft(2, '0')}';
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF121212),
      appBar: AppBar(
        title: Text('Time Tracker', style: TextStyle(fontWeight: FontWeight.bold, color: Colors.white)),
        backgroundColor: Color(0xFF1E1E1E),
        elevation: 0,
      ),
      body: Padding(
        padding: EdgeInsets.all(20),
        child: Column(
          children: [
            Container(
              padding: EdgeInsets.all(30),
              decoration: BoxDecoration(
                color: Color(0xFF1E1E1E),
                borderRadius: BorderRadius.circular(20),
                boxShadow: [BoxShadow(color: Colors.black26, blurRadius: 10, offset: Offset(0, 4))],
              ),
              child: Text(
                formatTime(seconds),
                style: TextStyle(
                  fontSize: 48,
                  fontWeight: FontWeight.bold,
                  fontFamily: 'monospace',
                  color: Color(0xFF00FF88),
                  shadows: [Shadow(color: Color(0xFF00FF88), blurRadius: 10)],
                ),
              ),
            ),
            SizedBox(height: 30),
            Container(
              decoration: BoxDecoration(
                gradient: LinearGradient(
                  colors: isRunning ? [Colors.orange[600]!, Colors.red[600]!] : [Color(0xFF6366F1), Color(0xFF8B5CF6)],
                ),
                borderRadius: BorderRadius.circular(25),
                boxShadow: [BoxShadow(color: Colors.black26, blurRadius: 8, offset: Offset(0, 4))],
              ),
              child: ElevatedButton(
                onPressed: () {
                  setState(() {
                    isRunning = !isRunning;
                    if (isRunning) seconds += 1;
                  });
                },
                style: ElevatedButton.styleFrom(
                  backgroundColor: Colors.transparent,
                  shadowColor: Colors.transparent,
                  padding: EdgeInsets.symmetric(horizontal: 40, vertical: 15),
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(25)),
                ),
                child: Text(
                  isRunning ? 'PAUSE' : 'START',
                  style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white),
                ),
              ),
            ),
            SizedBox(height: 30),
            TextField(
              controller: taskController,
              style: TextStyle(color: Colors.white),
              decoration: InputDecoration(
                hintText: 'Enter task description',
                hintStyle: TextStyle(color: Colors.grey[500]),
                border: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(15),
                  borderSide: BorderSide(color: Colors.grey[600]!),
                ),
                enabledBorder: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(15),
                  borderSide: BorderSide(color: Colors.grey[600]!),
                ),
                focusedBorder: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(15),
                  borderSide: BorderSide(color: Color(0xFF8B5CF6)),
                ),
                contentPadding: EdgeInsets.symmetric(horizontal: 15, vertical: 12),
                filled: true,
                fillColor: Color(0xFF1E1E1E),
              ),
            ),
            SizedBox(height: 20),
            if (isRunning)
              Container(
                padding: EdgeInsets.all(15),
                decoration: BoxDecoration(
                  color: Color(0xFF1E1E1E),
                  borderRadius: BorderRadius.circular(12),
                  border: Border.all(color: Color(0xFF00CED1)),
                  boxShadow: [BoxShadow(color: Color(0xFF00CED1).withOpacity(0.3), blurRadius: 10)],
                ),
                child: Row(
                  children: [
                    Expanded(
                      child: Column(
                        crossAxisAlignment: CrossAxisAlignment.start,
                        children: [
                          Text(currentTask, style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16, color: Colors.white)),
                          Text('${formatTime(seconds)} elapsed', style: TextStyle(color: Color(0xFF00CED1))),
                        ],
                      ),
                    ),
                    IconButton(
                      onPressed: () => setState(() => isRunning = false),
                      icon: Icon(Icons.stop, color: Colors.red[400]),
                    ),
                  ],
                ),
              ),
            SizedBox(height: 20),
            Expanded(
              child: ListView(
                children: [
                  Container(
                    margin: EdgeInsets.only(bottom: 8),
                    decoration: BoxDecoration(
                      color: Color(0xFF1E1E1E),
                      borderRadius: BorderRadius.circular(8),
                      border: Border(left: BorderSide(color: Color(0xFF8B5CF6), width: 4)),
                    ),
                    child: ListTile(
                      title: Text('UI Design', style: TextStyle(color: Colors.white)),
                      subtitle: Text('2h 30m • Today', style: TextStyle(color: Colors.grey[400])),
                      trailing: Icon(Icons.access_time, color: Color(0xFF8B5CF6)),
                    ),
                  ),
                  Container(
                    margin: EdgeInsets.only(bottom: 8),
                    decoration: BoxDecoration(
                      color: Color(0xFF1E1E1E),
                      borderRadius: BorderRadius.circular(8),
                      border: Border(left: BorderSide(color: Color(0xFF00CED1), width: 4)),
                    ),
                    child: ListTile(
                      title: Text('Code Review', style: TextStyle(color: Colors.white)),
                      subtitle: Text('1h 45m • Yesterday', style: TextStyle(color: Colors.grey[400])),
                      trailing: Icon(Icons.access_time, color: Color(0xFF00CED1)),
                    ),
                  ),
                  Container(
                    decoration: BoxDecoration(
                      color: Color(0xFF1E1E1E),
                      borderRadius: BorderRadius.circular(8),
                      border: Border(left: BorderSide(color: Color(0xFF00FF88), width: 4)),
                    ),
                    child: ListTile(
                      title: Text('Meeting', style: TextStyle(color: Colors.white)),
                      subtitle: Text('45m • Yesterday', style: TextStyle(color: Colors.grey[400])),
                      trailing: Icon(Icons.access_time, color: Color(0xFF00FF88)),
                    ),
                  ),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}
