      appBar: AppBar(
        backgroundColor: Colors.white,
        elevation: 0,
        title: Text('Budget Planner', style: TextStyle(color: Colors.grey[800], fontSize: 18, fontWeight: FontWeight.w300, letterSpacing: 0.5)),
        centerTitle: true,
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.symmetric(horizontal: 24, vertical: 16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            SizedBox(height: 32),
            Center(
              child: Column(
                children: [
                  Text('Total Balance', style: TextStyle(color: Colors.grey[500], fontSize: 14, fontWeight: FontWeight.w300)),
                  SizedBox(height: 12),
                  Text('4,250.00', style: TextStyle(color: Colors.grey[800], fontSize: 42, fontWeight: FontWeight.w200)),
                  SizedBox(height: 24),
                  Row(
                    mainAxisAlignment: MainAxisAlignment.center,
                    children: [
                      Icon(Icons.keyboard_arrow_left, color: Colors.grey[300], size: 16),
                      SizedBox(width: 16),
                      Text('March 2024', style: TextStyle(fontSize: 16, fontWeight: FontWeight.w300, color: Colors.grey[600])),
                      SizedBox(width: 16),
                      Icon(Icons.keyboard_arrow_right, color: Colors.grey[300], size: 16),
                    ],
                  ),
                ],
              ),
            ),
            SizedBox(height: 48),
            Text('Categories', style: TextStyle(fontSize: 16, fontWeight: FontWeight.w300, color: Colors.grey[700], letterSpacing: 0.5)),
            SizedBox(height: 24),
            Column(
              children: [
                _buildMinimalCategoryItem('Food', Color(0xFFFF9800), 450, 500),
                SizedBox(height: 20),
                _buildMinimalCategoryItem('Transport', Color(0xFF2196F3), 280, 300),
                SizedBox(height: 20),
                _buildMinimalCategoryItem('Shopping', Color(0xFF9C27B0), 180, 200),
                SizedBox(height: 20),
                _buildMinimalCategoryItem('Bills', Color(0xFFF44336), 520, 500),
              ],
            ),
            SizedBox(height: 48),
            Text('Recent Transactions', style: TextStyle(fontSize: 16, fontWeight: FontWeight.w300, color: Colors.grey[700], letterSpacing: 0.5)),
            SizedBox(height: 24),
            Column(
              children: [
                _buildMinimalTransactionItem('Starbucks', '-4.50', 'Today'),
                _buildMinimalTransactionItem('Uber Ride', '-12.30', 'Yesterday'),
                _buildMinimalTransactionItem('Grocery Store', '-67.20', 'Mar 15'),
                _buildMinimalTransactionItem('Salary', '+3,200', 'Mar 14'),
              ],
            ),
            SizedBox(height: 100),
          ],
        ),
      ),
      floatingActionButton: FloatingActionButton(
