  }

  Widget _buildMinimalCategoryItem(String title, Color color, double spent, double budget) {
    double percentage = spent / budget;
    Color statusColor = percentage > 1 ? Colors.red[300]! : (percentage > 0.8 ? Colors.orange[300]! : Colors.green[300]!);

    return Column(
      children: [
        Row(
          children: [
            Container(
              width: 8,
              height: 8,
              decoration: BoxDecoration(
                color: color,
                shape: BoxShape.circle,
              ),
            ),
            SizedBox(width: 16),
            Expanded(
              child: Text(title, style: TextStyle(fontSize: 15, fontWeight: FontWeight.w400, color: Colors.grey[700])),
            ),
            Text('\$${spent.toInt()}', style: TextStyle(fontSize: 14, fontWeight: FontWeight.w300, color: Colors.grey[800])),
            Text(' / \$${budget.toInt()}', style: TextStyle(fontSize: 12, fontWeight: FontWeight.w300, color: Colors.grey[500])),
          ],
        ),
        SizedBox(height: 8),
        Container(
          height: 2,
          child: LinearProgressIndicator(
            value: percentage > 1 ? 1 : percentage,
            backgroundColor: Colors.grey[200],
            valueColor: AlwaysStoppedAnimation<Color>(statusColor),
          ),
        ),
      ],
    );
  }

  Widget _buildMinimalTransactionItem(String title, String amount, String date) {
    return Container(
      padding: EdgeInsets.symmetric(vertical: 16),
      decoration: BoxDecoration(
        border: Border(bottom: BorderSide(color: Colors.grey[200]!, width: 0.5)),
      ),
      child: Row(
        children: [
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w400, fontSize: 15, color: Colors.grey[800])),
                SizedBox(height: 4),
                Text(date, style: TextStyle(color: Colors.grey[500], fontSize: 12, fontWeight: FontWeight.w300)),
              ],
            ),
          ),
          Text(amount, style: TextStyle(
            fontWeight: FontWeight.w300,
            fontSize: 15,
            color: amount.startsWith('+') ? Colors.green[600] : Colors.grey[700],
          )),
        ],
      ),
    );
  }
}
