import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFF5F5DC),
      appBar: AppBar(
        backgroundColor: Colors.brown[700],
        elevation: 2,
        leading: Icon(Icons.arrow_back, color: Colors.white),
        title: Text(
          'Compare Products',
          style: TextStyle(
            color: Colors.white,
            fontSize: 20,
            fontWeight: FontWeight.bold,
            fontFamily: 'Courier New',
          ),
        ),
        centerTitle: true,
      ),
      body: Padding(
        padding: EdgeInsets.all(16.0),
        child: SingleChildScrollView(
          scrollDirection: Axis.horizontal,
          child: Row(
            children: [
              _buildProductCard(
                'Smartphone X',
                '799',
                4.7,
                ['128GB Storage', '6.5" Display', 'Quad Camera', '5000mAh Battery'],
              ),
              SizedBox(width: 16),
              _buildProductCard(
                'Smartphone Y',
                '899',
                4.5,
                ['256GB Storage', '6.7" Display', 'Triple Camera', '4500mAh Battery'],
              ),
              SizedBox(width: 16),
              _buildProductCard(
                'Smartphone Z',
                '699',
                4.3,
                ['64GB Storage', '6.4" Display', 'Dual Camera', '4000mAh Battery'],
              ),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildProductCard(String name, String price, double rating, List<String> specs) {
    return Container(
      width: 280,
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(10),
        boxShadow: [
          BoxShadow(
            color: Colors.black.withOpacity(0.1),
            spreadRadius: 1,
            blurRadius: 5,
            offset: Offset(2, 2),
          ),
          BoxShadow(
            color: Colors.white.withOpacity(0.5),
            spreadRadius: 1,
            blurRadius: 5,
            offset: Offset(-2, -2),
          ),
        ],
      ),
      child: Padding(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Row(
              mainAxisAlignment: MainAxisAlignment.spaceBetween,
              children: [
                Container(
                  height: 160,
                  width: 160,
                  decoration: BoxDecoration(
                    color: Colors.grey[300],
                    borderRadius: BorderRadius.circular(10),
                  ),
                  child: Center(
                    child: Icon(Icons.phone_android, size: 70, color: Colors.grey[600]),
                  ),
                ),
                ElevatedButton(
                  onPressed: () {},
                  style: ElevatedButton.styleFrom(
                    backgroundColor: Colors.teal[600],
                    minimumSize: Size(100, 40),
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(8),
                    ),
                  ),
                  child: Text(
                    'Add to Cart',
                    style: TextStyle(
                      color: Colors.white,
                      fontSize: 14,
                      fontWeight: FontWeight.w500,
                      fontFamily: 'Courier New',
                    ),
                  ),
                ),
              ],
            ),
            SizedBox(height: 16),
            Text(
              name,
              style: TextStyle(
                fontSize: 18,
                fontWeight: FontWeight.bold,
                color: Colors.brown[800],
                fontFamily: 'Courier New',
              ),
            ),
            SizedBox(height: 8),
            Text(
              price,
              style: TextStyle(
                fontSize: 20,
                fontWeight: FontWeight.w600,
                color: Colors.red[600],
                fontFamily: 'Courier New',
              ),
            ),
            SizedBox(height: 8),
            Row(
              children: List.generate(5, (index) => Icon(
                index < rating.floor() ? Icons.star : Icons.star_border,
                color: Colors.amber[700],
                size: 20,
              )),
            ),
            SizedBox(height: 16),
            ...specs.map((spec) => Padding(
              padding: EdgeInsets.only(bottom: 4),
              child: Row(
                children: [
                  Icon(Icons.check, size: 16, color: Colors.black),
                  SizedBox(width: 8),
                  Expanded(
                    child: Text(
                      spec,
                      style: TextStyle(
                        fontSize: 14,
                        color: Colors.black,
                        fontFamily: 'Courier New',
                      ),
                    ),
                  ),
                ],
              ),
            )).toList(),
          ],
        ),
      ),
    );
  }
}
