            SizedBox(height: 26),

            // App Preferences
            Text('Preferences', style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Color(0xFFE11D48))),
            SizedBox(height: 14),
            _buildSettingItem(Icons.notifications, 'Notifications', Switch(value: _notificationsEnabled, activeColor: Color(0xFF06B6D4), onChanged: (val) => setState(() => _notificationsEnabled = val)), [Color(0xFF8B5FBF), Color(0xFFFF6B9D)]),
            _buildSettingItem(Icons.dark_mode, 'Dark Mode', Switch(value: _darkModeEnabled, activeColor: Color(0xFFFF6B9D), onChanged: (val) => setState(() => _darkModeEnabled = val)), [Color(0xFFFF6B9D), Color(0xFFFFB347)]),
