  Widget _buildSettingItem(IconData icon, String title, Widget? trailing, List<Color> gradientColors) {
    return Container(
      margin: EdgeInsets.only(bottom: 14),
      decoration: BoxDecoration(
        gradient: LinearGradient(colors: gradientColors),
        borderRadius: BorderRadius.circular(18),
        boxShadow: [BoxShadow(color: gradientColors[0].withOpacity(0.3), blurRadius: 8, offset: Offset(0, 4))],
      ),
      child: ListTile(
        contentPadding: EdgeInsets.symmetric(horizontal: 20, vertical: 6),
        leading: Icon(icon, color: Colors.white, size: 26),
        title: Text(title, style: TextStyle(fontSize: 17, color: Colors.white, fontWeight: FontWeight.w600)),
        trailing: trailing ?? Icon(Icons.arrow_forward_ios, size: 18, color: Colors.white),
        onTap: () {},
      ),
    );
