import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String selectedFilter = 'All';
  List<String> filters = ['All', 'Jewelry', 'Fashion', 'Watches', 'Accessories'];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF0A0A2A),
      appBar: AppBar(
        title: Text('Search', style: TextStyle(fontWeight: FontWeight.w500, fontFamily: 'PlayfairDisplay')),
        backgroundColor: Color(0xFF0A0A2A),
        foregroundColor: Colors.white,
        elevation: 0,
      ),
      body: Column(
        children: [
          Padding(
            padding: EdgeInsets.symmetric(horizontal: 16.0, vertical: 10.0),
            child: TextField(
              decoration: InputDecoration(
                hintText: 'Search products...',
                hintStyle: TextStyle(color: Colors.grey.shade400),
                prefixIcon: Icon(Icons.search, color: Color(0xFFD4AF37)),
                border: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(10),
                  borderSide: BorderSide(color: Colors.black),
                ),
                focusedBorder: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(10),
                  borderSide: BorderSide(color: Color(0xFFD4AF37), width: 2),
                ),
                filled: true,
                fillColor: Colors.white,
              ),
              style: TextStyle(color: Colors.black, fontFamily: 'PlayfairDisplay'),
            ),
          ),
          Container(
            height: 50,
            child: ListView.builder(
              scrollDirection: Axis.horizontal,
              padding: EdgeInsets.symmetric(horizontal: 16),
              itemCount: filters.length,
              itemBuilder: (context, index) {
                bool isSelected = selectedFilter == filters[index];
                return Padding(
                  padding: EdgeInsets.only(right: 8),
                  child: FilterChip(
                    label: Text(filters[index], style: TextStyle(fontFamily: 'PlayfairDisplay')),
                    selected: isSelected,
                    onSelected: (selected) {
                      setState(() {
                        selectedFilter = filters[index];
                      });
                    },
                    backgroundColor: Colors.black,
                    selectedColor: Color(0xFFD4AF37).withOpacity(0.3),
                    labelStyle: TextStyle(
                      color: isSelected ? Color(0xFFD4AF37) : Colors.white,
                      fontWeight: isSelected ? FontWeight.bold : FontWeight.normal,
                    ),
                  ),
                );
              },
            ),
          ),
          Expanded(
            child: GridView.builder(
              padding: EdgeInsets.all(16),
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                childAspectRatio: 0.7,
                crossAxisSpacing: 12,
                mainAxisSpacing: 12,
              ),
              itemCount: 8,
              itemBuilder: (context, index) {
                return Card(
                  color: Colors.white,
                  elevation: 3,
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(10)),
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      Expanded(
                        flex: 3,
                        child: Container(
                          decoration: BoxDecoration(
                            color: Colors.grey.shade200,
                            borderRadius: BorderRadius.vertical(top: Radius.circular(10)),
                          ),
                          child: Center(
                            child: Icon(Icons.image, size: 40, color: Colors.grey.shade400),
                          ),
                        ),
                      ),
                      Expanded(
                        flex: 2,
                        child: Padding(
                          padding: EdgeInsets.all(8),
                          child: Column(
                            crossAxisAlignment: CrossAxisAlignment.start,
                            children: [
                              Text(
                                'Product ${index + 1}',
                                style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, fontFamily: 'PlayfairDisplay'),
                                maxLines: 1,
                                overflow: TextOverflow.ellipsis,
                              ),
                              SizedBox(height: 4),
                              Text(
                                '\$${(index + 1) * 20}.99',
                                style: TextStyle(color: Color(0xFFD4AF37), fontWeight: FontWeight.bold, fontFamily: 'PlayfairDisplay'),
                              ),
                              SizedBox(height: 4),
                              Row(
                                children: List.generate(5, (i) => Icon(
                                  Icons.star,
                                  size: 12,
                                  color: i < 4 ? Colors.amber : Colors.grey.shade300,
                                )),
                              ),
                            ],
                          ),
                        ),
                      ),
                    ],
                  ),
                );
              },
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        backgroundColor: Color(0xFFD4AF37),
        child: Icon(Icons.diamond, color: Colors.white),
      ),
    );
  }
}
