import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.blue.shade900,
      body: SafeArea(
        child: Column(
          children: [
            AppBar(
              title: Text('Achievements', style: TextStyle(fontFamily: 'PressStart2P', color: Colors.white)),
              backgroundColor: Colors.blue.shade800,
              centerTitle: true,
            ),
            Container(
              margin: EdgeInsets.all(20),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Container(
                    width: 200,
                    height: 20,
                    child: LinearProgressIndicator(
                      value: 0.65,
                      backgroundColor: Colors.grey.shade700,
                      valueColor: AlwaysStoppedAnimation<Color>(Colors.greenAccent),
                    ),
                  ),
                  SizedBox(width: 10),
                  Text('13/20', style: TextStyle(fontFamily: 'PressStart2P', fontSize: 16, color: Colors.white)),
                ],
              ),
            ),
            Container(
              height: 40,
              child: ListView(
                scrollDirection: Axis.horizontal,
                padding: EdgeInsets.symmetric(horizontal: 20),
                children: ['All', 'Gaming', 'Learning', 'Social'].map((category) => 
                  Container(
                    margin: EdgeInsets.only(right: 10),
                    child: Chip(
                      label: Text(category, style: TextStyle(fontFamily: 'PressStart2P', color: Colors.white)),
                      backgroundColor: [Colors.pinkAccent, Colors.greenAccent, Colors.blueAccent, Colors.yellowAccent][category.length % 4],
                      side: BorderSide(color: Colors.white, width: 2),
                    ),
                  ),
                ).toList(),
              ),
            ),
            Expanded(
              child: GridView.builder(
                padding: EdgeInsets.all(20),
                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                  crossAxisCount: 2,
                  crossAxisSpacing: 10,
                  mainAxisSpacing: 10,
                  childAspectRatio: 1.0,
                ),
                itemCount: 8,
                itemBuilder: (context, index) {
                  bool isCompleted = index < 4;
                  return Container(
                    color: Colors.blue.shade800,
                    child: Opacity(
                      opacity: isCompleted ? 1.0 : 0.7,
                      child: Padding(
                        padding: EdgeInsets.all(10),
                        child: Column(
                          mainAxisAlignment: MainAxisAlignment.center,
                          children: [
                            CircleAvatar(
                              radius: 25,
                              backgroundColor: isCompleted ? (index < 2 ? Colors.amber : index < 3 ? Colors.grey.shade400 : Colors.orange.shade700) : Colors.grey.shade600,
                              child: Icon(isCompleted ? Icons.star : Icons.lock, color: Colors.white, size: 25),
                            ),
                            SizedBox(height: 10),
                            Text(
                              ['First Win', 'Quick Learner', 'Team Star', 'Socialite', 'Master', 'Explorer', 'Champion', 'Legend'][index],
                              style: TextStyle(fontFamily: 'PressStart2P', fontSize: 10, color: Colors.white),
                              textAlign: TextAlign.center,
                            ),
                          ],
                        ),
                      ),
                    ),
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
}
