import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.blue.shade50,
      body: SafeArea(
        child: Column(
          children: [
            AppBar(
              title: Text('Achievements', style: TextStyle(color: Colors.blue.shade900, fontWeight: FontWeight.bold)),
              backgroundColor: Colors.transparent,
              elevation: 0,
              centerTitle: true,
            ),
            Container(
              margin: EdgeInsets.all(20),
              padding: EdgeInsets.all(20),
              decoration: BoxDecoration(
                color: Colors.white,
                borderRadius: BorderRadius.circular(15),
                boxShadow: [BoxShadow(color: Colors.blue.shade100, blurRadius: 5)],
              ),
              child: Stack(
                alignment: Alignment.center,
                children: [
                  SizedBox(
                    width: 100,
                    height: 100,
                    child: CircularProgressIndicator(
                      value: 0.75,
                      strokeWidth: 8,
                      backgroundColor: Colors.grey.shade200,
                      valueColor: AlwaysStoppedAnimation<Color>(Colors.blue),
                    ),
                  ),
                  Text('15/20', style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Colors.blue.shade900)),
                ],
              ),
            ),
            Wrap(
              spacing: 10,
              runSpacing: 10,
              alignment: WrapAlignment.center,
              children: ['All', 'Gaming', 'Learning', 'Social'].map((category) => 
                Chip(
                  label: Text(category, style: TextStyle(color: Colors.blue.shade900)),
                  backgroundColor: Colors.white,
                  side: BorderSide(color: Colors.blue.shade900),
                ),
              ).toList(),
            ),
            Expanded(
              child: ListView.builder(
                padding: EdgeInsets.all(20),
                itemCount: 8,
                itemBuilder: (context, index) {
                  bool isCompleted = index < 5;
                  return GestureDetector(
                    onTap: () {},
                    child: Container(
                      margin: EdgeInsets.only(bottom: 15),
                      decoration: BoxDecoration(
                        color: Colors.white,
                        borderRadius: BorderRadius.circular(15),
                        boxShadow: [BoxShadow(color: isCompleted ? Colors.blue.shade200 : Colors.grey.shade200, blurRadius: 5)],
                      ),
                      child: Opacity(
                        opacity: isCompleted ? 1.0 : 0.7,
                        child: ListTile(
                          leading: CircleAvatar(
                            backgroundColor: isCompleted ? (index < 2 ? Colors.amber : index < 4 ? Colors.grey.shade400 : Colors.orange.shade700) : Colors.grey.shade300,
                            child: Icon(isCompleted ? Icons.star : Icons.lock, color: Colors.white),
                          ),
                          title: Text(
                            ['First Win', 'Quick Learner', 'Team Star', 'Socialite', 'Master', 'Explorer', 'Champion', 'Legend'][index],
                            style: TextStyle(fontWeight: FontWeight.bold, color: Colors.blue.shade900),
                          ),
                          subtitle: Text(
                            ['Win a game', 'Complete 5 lessons', 'Join a team', 'Connect with 10 friends', 'Master a skill', 'Explore 10 places', 'Win 10 games', 'Top leaderboard'][index],
                            style: TextStyle(fontSize: 12),
                          ),
                        ),
                      ),
                    ),
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
}
