import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool showAnswer = false;
  int currentCard = 1;
  int totalCards = 10;
  bool isFavorite = false;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      body: Container(
        decoration: BoxDecoration(
          image: DecorationImage(
            image: AssetImage('assets/grid.png'), // Note: Asset image not included
            fit: BoxFit.cover,
            opacity: 0.1,
          ),
        ),
        child: Column(
          children: [
            AppBar(
              backgroundColor: Colors.white,
              elevation: 0,
              title: Text('Math Formulas', style: TextStyle(fontFamily: 'Times New Roman', color: Colors.black87, fontSize: 22)),
              actions: [
                IconButton(icon: Icon(Icons.settings, color: Colors.blue[800]), onPressed: () {}),
                IconButton(icon: Icon(Icons.menu, color: Colors.blue[800]), onPressed: () {}),
              ],
            ),
            Padding(
              padding: EdgeInsets.all(16),
              child: Column(
                children: [
                  Text('$currentCard of $totalCards', style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.blue[800])),
                  Container(height: 2, color: Colors.blue[800], width: 50),
                ],
              ),
            ),
            Expanded(
              child: Center(
                child: Stack(
                  alignment: Alignment.center,
                  children: [
                    Container(
                      width: 300,
                      height: 180,
                      decoration: BoxDecoration(
                        color: Colors.white,
                        border: Border.all(color: Colors.blue[800]!, width: 2),
                        boxShadow: [BoxShadow(color: Colors.black12, blurRadius: 6, offset: Offset(0, 2))],
                      ),
                      child: Center(
                        child: Padding(
                          padding: EdgeInsets.all(20),
                          child: Text(
                            showAnswer ? 'a² + b² = c²' : 'What is the Pythagorean theorem?',
                            style: TextStyle(fontSize: 18, fontFamily: 'Times New Roman', color: Colors.black87),
                            textAlign: TextAlign.center,
                          ),
                        ),
                      ),
                    ),
                    Positioned(
                      top: 0,
                      left: 10,
                      child: IconButton(
                        icon: Icon(Icons.chevron_left, color: Colors.blue[800]),
                        onPressed: () {},
                      ),
                    ),
                    Positioned(
                      top: 0,
                      right: 10,
                      child: IconButton(
                        icon: Icon(Icons.chevron_right, color: Colors.blue[800]),
                        onPressed: () {},
                      ),
                    ),
                  ],
                ),
              ),
            ),
            Padding(
              padding: EdgeInsets.symmetric(vertical: 20),
              child: ElevatedButton(
                onPressed: () {
                  setState(() {
                    showAnswer = !showAnswer;
                  });
                },
                style: ElevatedButton.styleFrom(
                  backgroundColor: Colors.blue[800],
                  padding: EdgeInsets.symmetric(horizontal: 50, vertical: 12),
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                ),
                child: Text('Flip Card', style: TextStyle(color: Colors.white, fontSize: 16)),
              ),
            ),
            Container(
              padding: EdgeInsets.all(16),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  TextButton(
                    child: Text('Shuffle', style: TextStyle(color: Colors.blue[800], decoration: TextDecoration.underline)),
                    onPressed: () {},
                  ),
                  TextButton(
                    child: Text('Restart', style: TextStyle(color: Colors.blue[800], decoration: TextDecoration.underline)),
                    onPressed: () {},
                  ),
                  TextButton(
                    child: Text(isFavorite ? 'Unfavorite' : 'Favorite', style: TextStyle(color: Colors.blue[800], decoration: TextDecoration.underline)),
                    onPressed: () {
                      setState(() {
                        isFavorite = !isFavorite;
                      });
                    },
                  ),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}
