import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isRunning = false;
  int seconds = 0;
  String currentTask = 'Task 1';
  TextEditingController taskController = TextEditingController();

  String formatTime(int totalSeconds) {
    int hours = totalSeconds ~/ 3600;
    int minutes = (totalSeconds % 3600) ~/ 60;
    int secs = totalSeconds % 60;
    return '${hours.toString().padLeft(2, '0')}:${minutes.toString().padLeft(2, '0')}:${secs.toString().padLeft(2, '0')}';
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[200],
      appBar: AppBar(
        title: Text('Time Tracker', style: TextStyle(fontWeight: FontWeight.w600)),
        backgroundColor: Colors.teal[600],
        foregroundColor: Colors.white,
        elevation: 0,
      ),
      body: Padding(
        padding: EdgeInsets.all(8),
        child: Column(
          children: [
            Text(
              formatTime(seconds),
              style: TextStyle(
                fontSize: 24,
                fontWeight: FontWeight.bold,
                fontFamily: 'RobotoMono',
                color: Colors.teal[900],
              ),
            ),
            SizedBox(height: 8),
            ElevatedButton(
              onPressed: () => setState(() => isRunning = !isRunning),
              style: ElevatedButton.styleFrom(
                backgroundColor: isRunning ? Colors.orange[600] : Colors.teal[600],
                padding: EdgeInsets.symmetric(horizontal: 16, vertical: 8),
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
              ),
              child: Text(
                isRunning ? 'PAUSE' : 'START',
                style: TextStyle(fontSize: 14, color: Colors.white),
              ),
            ),
            SizedBox(height: 8),
            TextField(
              controller: taskController,
              decoration: InputDecoration(
                hintText: 'Task',
                border: OutlineInputBorder(borderRadius: BorderRadius.circular(8)),
                contentPadding: EdgeInsets.symmetric(horizontal: 8, vertical: 8),
              ),
            ),
            SizedBox(height: 8),
            if (isRunning)
              Card(
                color: Colors.teal[50],
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                child: Padding(
                  padding: EdgeInsets.all(8),
                  child: Row(
                    children: [
                      Expanded(
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(currentTask, style: TextStyle(fontSize: 14, fontWeight: FontWeight.w600)),
                            Text('${formatTime(seconds)} elapsed', style: TextStyle(color: Colors.teal[700])),
                          ],
                        ),
                      ),
                      IconButton(
                        onPressed: () => setState(() => isRunning = false),
                        icon: Icon(Icons.stop, color: Colors.red[600]),
                        padding: EdgeInsets.zero,
                      ),
                    ],
                  ),
                ),
              ),
            SizedBox(height: 8),
            Expanded(
              child: ListView.separated(
                itemCount: 3,
                separatorBuilder: (context, index) => SizedBox(height: 4),
                itemBuilder: (context, index) => ListTile(
                  title: Text('Task ${index + 1}', style: TextStyle(fontSize: 14)),
                  subtitle: Text('1h ${index * 15}m â€¢ Today', style: TextStyle(fontSize: 12)),
                  contentPadding: EdgeInsets.symmetric(horizontal: 8),
                  tileColor: Colors.white,
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(4)),
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }
}
