import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFF5F5F5),
      appBar: AppBar(
        backgroundColor: Color(0xFFF5F5F5),
        elevation: 1,
        title: Text(
          'CATEGORIES',
          style: TextStyle(
            color: Colors.black87,
            fontSize: 20,
            fontWeight: FontWeight.w400,
            letterSpacing: 2.0,
          ),
        ),
        centerTitle: true,
      ),
      body: Padding(
        padding: EdgeInsets.all(16.0),
        child: Column(
          children: [
            Row(
              children: [
                Expanded(
                  flex: 2,
                  child: _buildCategoryCard(
                    Icons.shopping_bag,
                    'Electronics',
                    Colors.blueGrey[600]!,
                    Colors.blueGrey[100]!,
                    180,
                  ),
                ),
                SizedBox(width: 12),
                Expanded(
                  flex: 1,
                  child: Column(
                    children: [
                      _buildCategoryCard(
                        Icons.headphones,
                        'Audio',
                        Colors.green[600]!,
                        Colors.green[100]!,
                        85,
                      ),
                      SizedBox(height: 12),
                      _buildCategoryCard(
                        Icons.book,
                        'Books',
                        Colors.amber[700]!,
                        Colors.amber[100]!,
                        83,
                      ),
                    ],
                  ),
                ),
              ],
            ),
            SizedBox(height: 12),
            Row(
              children: [
                Expanded(
                  flex: 1,
                  child: Column(
                    children: [
                      _buildCategoryCard(
                        Icons.laptop,
                        'Computers',
                        Colors.indigo[600]!,
                        Colors.indigo[100]!,
                        120,
                      ),
                      SizedBox(height: 12),
                      _buildCategoryCard(
                        Icons.home,
                        'Home',
                        Colors.teal[600]!,
                        Colors.teal[100]!,
                        90,
                      ),
                    ],
                  ),
                ),
                SizedBox(width: 12),
                Expanded(
                  flex: 2,
                  child: _buildCategoryCard(
                    Icons.checkroom,
                    'Fashion',
                    Colors.pink[600]!,
                    Colors.pink[100]!,
                    222,
                  ),
                ),
              ],
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildCategoryCard(IconData icon, String title, Color iconColor, Color borderColor, double height) {
    return Container(
      height: height,
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(8),
        border: Border.all(
          color: borderColor,
          width: 2,
        ),
        boxShadow: [
          BoxShadow(
            color: Colors.black.withOpacity(0.04),
            blurRadius: 6,
            offset: Offset(0, 2),
          ),
        ],
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Icon(
            icon,
            size: height > 150 ? 45 : 30,
            color: iconColor,
          ),
          SizedBox(height: 8),
          Text(
            title,
            style: TextStyle(
              fontSize: height > 150 ? 16 : 13,
              fontWeight: FontWeight.w500,
              color: Colors.grey[700],
              letterSpacing: 0.5,
            ),
          ),
        ],
      ),
    );
  }
}
