import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[200],
      appBar: AppBar(
        title: Text('Expense Tracker', style: TextStyle(color: Colors.black87, fontWeight: FontWeight.w600)),
        backgroundColor: Colors.grey[200],
        elevation: 0,
        centerTitle: true,
      ),
      body: Column(
        children: [
          Container(
            margin: EdgeInsets.all(16),
            padding: EdgeInsets.all(20),
            decoration: BoxDecoration(
              color: Colors.white,
              borderRadius: BorderRadius.circular(8),
            ),
            child: Column(
              children: [
                Text('Current Balance', style: TextStyle(fontSize: 16, color: Colors.grey[600])),
                SizedBox(height: 8),
                Text('2,450.00', style: TextStyle(fontSize: 32, fontWeight: FontWeight.w600, color: Colors.black87)),
              ],
            ),
          ),
          Container(
            height: 100,
            margin: EdgeInsets.symmetric(vertical: 8),
            child: ListView(
              scrollDirection: Axis.horizontal,
              padding: EdgeInsets.symmetric(horizontal: 16),
              children: [
                _buildCategoryCard('Food', Icons.restaurant, Colors.purple[100]!),
                _buildCategoryCard('Transport', Icons.directions_car, Colors.teal[100]!),
                _buildCategoryCard('Shopping', Icons.shopping_bag, Colors.orange[100]!),
                _buildCategoryCard('Bills', Icons.receipt, Colors.blue[100]!),
              ],
            ),
          ),
          Expanded(
            child: ListView.separated(
              padding: EdgeInsets.all(16),
              itemCount: 4,
              separatorBuilder: (context, index) => SizedBox(height: 12),
              itemBuilder: (context, index) {
                final transactions = [
                  {'title': 'Grocery Store', 'category': 'Food', 'date': 'Today', 'amount': '-45.20', 'icon': Icons.restaurant, 'color': Colors.purple[100]},
                  {'title': 'Gas Station', 'category': 'Transport', 'date': 'Yesterday', 'amount': '-32.50', 'icon': Icons.directions_car, 'color': Colors.teal[100]},
                  {'title': 'Online Shopping', 'category': 'Shopping', 'date': '2 days ago', 'amount': '-89.99', 'icon': Icons.shopping_bag, 'color': Colors.orange[100]},
                  {'title': 'Electricity Bill', 'category': 'Bills', 'date': '3 days ago', 'amount': '-125.00', 'icon': Icons.receipt, 'color': Colors.blue[100]},
                ];
                return _buildTransactionItem(
                  transactions[index]['title'] as String,
                  transactions[index]['category'] as String,
                  transactions[index]['date'] as String,
                  transactions[index]['amount'] as String,
                  transactions[index]['icon'] as IconData,
                  transactions[index]['color'] as Color,
                );
              },
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton(
