import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isRunning = false;
  int seconds = 0;
  String currentTask = 'Project Planning';
  TextEditingController taskController = TextEditingController();

  String formatTime(int totalSeconds) {
    int hours = totalSeconds ~/ 3600;
    int minutes = (totalSeconds % 3600) ~/ 60;
    int secs = totalSeconds % 60;
    return '${hours.toString().padLeft(2, '0')}:${minutes.toString().padLeft(2, '0')}:${secs.toString().padLeft(2, '0')}';
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        title: Text('Time Tracker', style: TextStyle(fontWeight: FontWeight.w600)),
        backgroundColor: Colors.teal[700],
        foregroundColor: Colors.white,
        elevation: 0,
      ),
      body: Padding(
        padding: EdgeInsets.all(16),
        child: Column(
          children: [
            Container(
              padding: EdgeInsets.all(24),
              decoration: BoxDecoration(
                color: Colors.teal[50],
                borderRadius: BorderRadius.circular(16),
                boxShadow: [BoxShadow(color: Colors.grey[200]!, blurRadius: 8)],
              ),
              child: Text(
                formatTime(seconds),
                style: TextStyle(
                  fontSize: 48,
                  fontWeight: FontWeight.bold,
                  fontFamily: 'RobotoMono',
                  color: Colors.teal[900],
                ),
              ),
            ),
            SizedBox(height: 24),
            AnimatedContainer(
              duration: Duration(milliseconds: 300),
              child: ElevatedButton(
                onPressed: () => setState(() => isRunning = !isRunning),
                style: ElevatedButton.styleFrom(
                  backgroundColor: isRunning ? Colors.red[600] : Colors.green[600],
                  shape: CircleBorder(),
                  padding: EdgeInsets.all(20),
                ),
                child: Text(
                  isRunning ? 'PAUSE' : 'START',
                  style: TextStyle(fontSize: 18, color: Colors.white),
                ),
              ),
            ),
            SizedBox(height: 24),
            TextField(
              controller: taskController,
              decoration: InputDecoration(
                hintText: 'Add task',
                filled: true,
                fillColor: Colors.teal[50],
                border: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(12),
                  borderSide: BorderSide.none,
                ),
              ),
            ),
            SizedBox(height: 16),
            if (isRunning)
              Card(
                color: Colors.teal[50],
                elevation: 2,
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
                child: Padding(
                  padding: EdgeInsets.all(12),
                  child: Row(
                    children: [
                      Expanded(
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(currentTask, style: TextStyle(fontSize: 16, fontWeight: FontWeight.w600)),
                            Text('${formatTime(seconds)} elapsed', style: TextStyle(color: Colors.teal[700])),
                          ],
                        ),
                      ),
                      IconButton(
                        onPressed: () => setState(() => isRunning = false),
                        icon: Icon(Icons.stop, color: Colors.red[600]),
                      ),
                    ],
                  ),
                ),
              ),
            SizedBox(height: 16),
            Expanded(
              child: ListView.separated(
                itemCount: 3,
                separatorBuilder: (context, index) => SizedBox(height: 8),
                itemBuilder: (context, index) => ListTile(
                  title: Text('Task ${index + 1}'),
                  subtitle: Text('1h ${index * 15}m â€¢ Today'),
                  tileColor: Colors.grey[100],
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }
}
