  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFF5F5F5),
      appBar: AppBar(
        backgroundColor: Color(0xFFF5F5F5),
        elevation: 0,
        title: Text('Budget Planner', style: TextStyle(color: Color(0xFFAB47BC), fontSize: 22, fontWeight: FontWeight.w700, fontFamily: 'SourceSansPro')),
        centerTitle: true,
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(12),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Container(
              width: double.infinity,
              padding: EdgeInsets.all(12),
              decoration: BoxDecoration(
                color: Colors.white,
                borderRadius: BorderRadius.circular(8),
                boxShadow: [BoxShadow(color: Colors.grey.withOpacity(0.1), spreadRadius: 1, blurRadius: 2)],
              ),
              child: Column(
                children: [
                  Text('Total Balance', style: TextStyle(color: Colors.grey[600], fontSize: 14)),
                  SizedBox(height: 8),
                  Text('4,250.00', style: TextStyle(color: Color(0xFFAB47BC), fontSize: 28, fontWeight: FontWeight.bold)),
                  SizedBox(height: 12),
                  Row(
                    mainAxisAlignment: MainAxisAlignment.center,
                    children: [
                      Icon(Icons.arrow_back_ios, color: Color(0xFFAB47BC), size: 18),
                      SizedBox(width: 8),
                      Text('August 2025', style: TextStyle(fontSize: 16, color: Colors.grey[600], fontWeight: FontWeight.w600)),
                      SizedBox(width: 8),
                      Icon(Icons.arrow_forward_ios, color: Color(0xFFAB47BC), size: 18),
                    ],
                  ),
                ],
              ),
            ),
            SizedBox(height: 16),
            Text('Categories', style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.grey[800])),
            SizedBox(height: 12),
            SingleChildScrollView(
              scrollDirection: Axis.horizontal,
              child: Row(
                children: [
                  _buildCategoryCard('Food', Icons.restaurant, Color(0xFFAB47BC), 450, 500, Colors.green),
                  SizedBox(width: 12),
                  _buildCategoryCard('Transport', Icons.directions_car, Color(0xFFAB47BC), 280, 300, Colors.orange),
                  SizedBox(width: 12),
                  _buildCategoryCard('Shopping', Icons.shopping_bag, Color(0xFFAB47BC), 180, 200, Colors.green),
                  SizedBox(width: 12),
                  _buildCategoryCard('Bills', Icons.receipt_long, Color(0xFFAB47BC), 520, 500, Colors.red),
                ],
              ),
            ),
            SizedBox(height: 16),
            Text('Recent Transactions', style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.grey[800])),
            SizedBox(height: 12),
            Container(
              decoration: BoxDecoration(
                color: Colors.white,
                borderRadius: BorderRadius.circular(8),
                boxShadow: [BoxShadow(color: Colors.grey.withOpacity(0.1), spreadRadius: 1, blurRadius: 2)],
              ),
              child: Column(
                children: [
                  _buildTransactionItem('Starbucks', Icons.local_cafe, '-4.50', 'Today'),
                  _buildTransactionItem('Uber Ride', Icons.local_taxi, '-12.30', 'Yesterday'),
