import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF121212),
      appBar: AppBar(
        backgroundColor: Color(0xFF121212),
        elevation: 0,
        title: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Budget Planner', style: TextStyle(color: Colors.white, fontSize: 24, fontWeight: FontWeight.w700, fontFamily: 'Roboto')),
            Container(height: 2, width: 100, color: Color(0xFF00BCD4)),
          ],
        ),
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(20),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Total Balance', style: TextStyle(color: Colors.grey[400], fontSize: 16)),
            SizedBox(height: 8),
            Text('4,250.00', style: TextStyle(color: Color(0xFF00BCD4), fontSize: 32, fontWeight: FontWeight.bold)),
            SizedBox(height: 16),
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                Icon(Icons.arrow_back_ios, color: Color(0xFFE91E63), size: 22),
                SizedBox(width: 8),
                Text('August 2025', style: TextStyle(fontSize: 18, color: Colors.white, fontWeight: FontWeight.w600)),
                SizedBox(width: 8),
                Icon(Icons.arrow_forward_ios, color: Color(0xFFE91E63), size: 22),
              ],
            ),
            SizedBox(height: 24),
            Text('Categories', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white)),
            SizedBox(height: 16),
            Column(
              children: [
                _buildCategoryCard('Food', Icons.restaurant, Color(0xFF00BCD4), 450, 500, Colors.green),
                SizedBox(height: 12),
                _buildCategoryCard('Transport', Icons.directions_car, Color(0xFF00BCD4), 280, 300, Color(0xFFE91E63)),
                SizedBox(height: 12),
                _buildCategoryCard('Shopping', Icons.shopping_bag, Color(0xFF00BCD4), 180, 200, Colors.green),
                SizedBox(height: 12),
                _buildCategoryCard('Bills', Icons.receipt_long, Color(0xFF00BCD4), 520, 500, Colors.red),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Transactions', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white)),
            SizedBox(height: 16),
            Container(
              decoration: BoxDecoration(
                color: Color(0xFF1E1E1E),
                borderRadius: BorderRadius.circular(8),
              ),
              child: Column(
                children: [
                  _buildTransactionItem('Starbucks', Icons.local_cafe, '-4.50', 'Today'),
                  _buildTransactionItem('Uber Ride', Icons.local_taxi, '-12.30', 'Yesterday'),
                  _buildTransactionItem('Grocery Store', Icons.shopping_cart, '-67.20', 'Aug 7'),
                  _buildTransactionItem('Salary', Icons.account_balance_wallet, '+3,200', 'Aug 1'),
                ],
              ),
            ),
          ],
        ),
      ),
      floatingActionButton: Row(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          FloatingActionButton(
            heroTag: 'expense',
            onPressed: () {},
            backgroundColor: Color(0xFF00BCD4),
            child: Icon(Icons.remove, color: Colors.white),
          ),
          SizedBox(width: 16),
          FloatingActionButton(
            heroTag: 'income',
            onPressed: () {},
            backgroundColor: Color(0xFFE91E63),
            child: Icon(Icons.add, color: Colors.white),
          ),
        ],
      ),
    );
  }

  Widget _buildCategoryCard(String title, IconData icon, Color color, double spent, double budget, Color statusColor) {
    double percentage = spent / budget;
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Color(0xFF1E1E1E),
        borderRadius: BorderRadius.circular(8),
        border: Border.all(color: statusColor.withOpacity(0.5)),
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            children: [
              Container(
                padding: EdgeInsets.all(8),
                decoration: BoxDecoration(
                  color: color.withOpacity(0.2),
                  borderRadius: BorderRadius.circular(8),
                ),
                child: Icon(icon, color: color, size: 20),
              ),
              Spacer(),
              SizedBox(
                width: 50,
                child: LinearProgressIndicator(
                  value: percentage > 1 ? 1 : percentage,
                  backgroundColor: Colors.grey[700],
                  valueColor: AlwaysStoppedAnimation<Color>(statusColor),
                  minHeight: 2,
                ),
              ),
            ],
          ),
          SizedBox(height: 12),
          Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, color: Colors.white)),
          SizedBox(height: 4),
          Text('${spent.toInt()}/$${budget.toInt()}', style: TextStyle(color: Colors.grey[400], fontSize: 12)),
          Text('$${(budget - spent).toInt()} left', style: TextStyle(color: statusColor, fontSize: 11, fontWeight: FontWeight.w500)),
        ],
      ),
    );
  }

  Widget _buildTransactionItem(String title, IconData icon, String amount, String date) {
    return Padding(
      padding: EdgeInsets.symmetric(horizontal: 16, vertical: 12),
      child: Row(
        children: [
          Container(
            padding: EdgeInsets.all(8),
            decoration: BoxDecoration(
              color: Colors.grey[800],
              borderRadius: BorderRadius.circular(8),
            ),
            child: Icon(icon, color: Colors.white, size: 20),
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w500, color: Colors.white)),
                Text(date, style: TextStyle(color: Colors.grey[400], fontSize: 12)),
              ],
            ),
          ),
          Text(amount, style: TextStyle(
            fontWeight: FontWeight.w600,
            color: amount.startsWith('+') ? Color(0xFF00BCD4) : Color(0xFFE91E63),
          )),
        ],
      ),
    );
  }
}
