import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isPlaying = false;
  double currentPosition = 0.3;
  double volume = 0.7;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        backgroundColor: Colors.transparent,
        elevation: 0,
        leading: Icon(Icons.keyboard_arrow_down, color: Colors.white70),
        actions: [
          Icon(Icons.more_vert, color: Colors.white70),
        ],
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Container(
              width: 260,
              height: 260,
              decoration: BoxDecoration(
                shape: BoxShape.circle,
                border: Border.all(color: Colors.white70, width: 1),
                image: DecorationImage(
                  image: NetworkImage('https://via.placeholder.com/260x260/FFFFFF/000000?text=Album'),
                  fit: BoxFit.cover,
                ),
              ),
            ),
            SizedBox(height: 20),
            Text(
              'Monochrome Melodies',
              style: TextStyle(
                fontSize: 26,
                fontWeight: FontWeight.bold,
                color: Colors.white,
              ),
            ),
            SizedBox(height: 8),
            Text(
              'Silent Tunes',
              style: TextStyle(
                fontSize: 16,
                color: Colors.white54,
              ),
            ),
            SizedBox(height: 20),
            SliderTheme(
              data: SliderTheme.of(context).copyWith(
                thumbShape: RoundSliderThumbShape(enabledThumbRadius: 6),
                trackHeight: 3,
              ),
              child: Slider(
                value: currentPosition,
                onChanged: (value) => setState(() => currentPosition = value),
                activeColor: Colors.white,
                inactiveColor: Colors.white24,
              ),
            ),
            Padding(
              padding: const EdgeInsets.symmetric(horizontal: 16),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceBetween,
                children: [
                  Text('1:30', style: TextStyle(fontSize: 12, color: Colors.white70, fontFamily: 'monospace')),
                  Text('4:00', style: TextStyle(fontSize: 12, color: Colors.white70, fontFamily: 'monospace')),
                ],
              ),
            ),
            SizedBox(height: 20),
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                OutlinedButton(
                  onPressed: () {},
                  child: Icon(Icons.skip_previous, size: 32, color: Colors.white70),
                  style: OutlinedButton.styleFrom(
                    side: BorderSide(color: Colors.white70),
                  ),
                ),
                SizedBox(width: 20),
                OutlinedButton(
                  onPressed: () => setState(() => isPlaying = !isPlaying),
                  child: Icon(isPlaying ? Icons.pause : Icons.play_arrow, size: 40, color: Colors.white70),
                  style: OutlinedButton.styleFrom(
                    side: BorderSide(color: Colors.white70),
                  ),
                ),
                SizedBox(width: 20),
                OutlinedButton(
                  onPressed: () {},
                  child: Icon(Icons.skip_next, size: 32, color: Colors.white70),
                  style: OutlinedButton.styleFrom(
                    side: BorderSide(color: Colors.white70),
                  ),
                ),
              ],
            ),
            SizedBox(height: 20),
            Row(
              children: [
                Icon(Icons.volume_up, color: Colors.white70),
                Expanded(
                  child: Slider(
                    value: volume,
                    onChanged: (value) => setState(() => volume = value),
                    activeColor: Colors.white,
                    inactiveColor: Colors.white24,
                  ),
                ),
              ],
            ),
          ],
        ),
      ),
    );
  }
}
